(352, '_transient_feed_acfb94f16410e8193f15ed3900b38226', 'a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:52:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n\n \n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:3:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"WPForms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"https://wpforms.com\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"Drag & Drop WordPress Form Builder\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 09 Nov 2018 23:17:35 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-US\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"https://wordpress.org/?v=4.9.6\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"image\";a:1:{i:0;a:6:{s:4:\"data\";s:11:\"\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:3:\"url\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://wpforms.com/wp-content/uploads/2016/02/cropped-sullie-favicon-32x32.png\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"WPForms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"https://wpforms.com\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"width\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:6:\"height\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}s:4:\"item\";a:10:{i:0;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"10+ Simple Ways to Design Your Forms and Boost Conversions\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:75:\"https://wpforms.com/simple-ways-to-design-your-forms-and-boost-conversions/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wpforms.com/simple-ways-to-design-your-forms-and-boost-conversions/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 08 Nov 2018 13:00:14 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:9:\"Marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:20:\"form conversion rate\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:15:\"mobile friendly\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:23:\"mobile form conversions\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:15:\"content upgrade\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=177121\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:587:\"<p>Want to learn some simple ways to design your forms so you can boost conversions? Knowing what appeals to site visitors and makes them want to fill your forms out is important for preventing form abandonment. In this article, we’ll show you some of the easiest ways to design your WordPress forms so people want [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/simple-ways-to-design-your-forms-and-boost-conversions/\">10+ Simple Ways to Design Your Forms and Boost Conversions</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:16015:\"<p>Want to learn some simple ways to design your forms so you can boost conversions? Knowing what appeals to site visitors and makes them want to fill your forms out is important for preventing form abandonment.</p>\n<p>In this article, we’ll show you some of the easiest ways to design your WordPress forms so people want to fill them out.</p>\n<h3>1. Think Mobile</h3>\n<p>It should come as no surprise that making your forms mobile friendly is a simple way to make your forms easier to fill out by those on the go. And, in case you haven’t heard, Google recently announced that starting in July 2018, <a href=\"https://webmasters.googleblog.com/2018/01/using-page-speed-in-mobile-search.html\" target=\"_blank\" rel=\"nofollow noopener\">mobile page speed</a> will be used as a ranking factor.</p>\n<p>That means your forms, and your entire website, should be 100 percent <a href=\"https://wpforms.com/mobile-lead-generation/\">optimized to be mobile friendly as soon as possible</a>.</p>\n<p>Especially since mobile devices account for <a href=\"https://www.statista.com/topics/779/mobile-internet/\" target=\"_blank\" rel=\"nofollow noopener\">51.2% of all internet traffic</a> and people from around the world love spending time on mobile devices of all kinds.</p>\n<p><img class=\"alignnone size-full wp-image-177123\" src=\"https://wpforms.com/wp-content/uploads/2018/04/Mobile-Time-Spent-Online.png\" alt=\"Mobile Time Spent Online\" width=\"562\" height=\"237\" srcset=\"https://wpforms.com/wp-content/uploads/2018/04/Mobile-Time-Spent-Online.png 562w, https://wpforms.com/wp-content/uploads/2018/04/Mobile-Time-Spent-Online-300x127.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Not sure where to start? Check out these helpful tips:</p>\n<ul>\n<li>Use a responsive signup form such as the ones that you can create using <a href=\"https://wpforms.com/\">WPForms</a></li>\n<li>Use a <a href=\"https://wpforms.com/how-to-create-a-responsive-contact-form-with-multiple-columns/\">single column layout</a> so those on small devices can easily navigate the form fields</li>\n<li>Track your mobile form conversions using the <a href=\"https://www.monsterinsights.com/addon/forms/\" target=\"_blank\" rel=\"noopener\">MonsterInsights Forms addon</a> to make sure visitors are converting</li>\n<li>Add a phone number call to action making it easy for those on their phones to contact you</li>\n</ul>\n<p>For more help boosting mobile form conversions, check out our <a href=\"https://wpforms.com/simple-tricks-to-boost-mobile-form-conversions/\">four simple tricks</a>.</p>\n<h3>2. Create a Standout Call to Action Button</h3>\n<p>Getting people to fill out your website’s forms, and actually submit them, all boils down to the call to action button.</p>\n<p>After all, if people don’t submit the form, despite filling the entire thing out, you lose out on an opportunity to reach out to a customer, add another person to your email list, or secure another sale.</p>\n<p>In order to <a href=\"https://wpforms.com/how-to-customize-button-styles-with-css-with-examples/\">make your call to action button stand out</a>, try the following:</p>\n<ul>\n<li>Use bold colors that stand out from the rest of the form</li>\n<li>Make sure the copy within the button encourages a click and is an action phrase, which can easily be customized with WPForms</li>\n<li>Use details in the button’s copy so your site visitors know exactly what they’re signing up for</li>\n</ul>\n<p>Take a look at this example on Brian Dean’s website <a href=\"https://backlinko.com/\" target=\"_blank\" rel=\"nofollow noopener\">Backlinko</a>:</p>\n<p><img class=\"alignnone size-full wp-image-309664\" src=\"https://wpforms.com/wp-content/uploads/2018/11/cta-button-example.png\" alt=\"cta button example\" width=\"562\" height=\"308\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/cta-button-example.png 562w, https://wpforms.com/wp-content/uploads/2018/11/cta-button-example-300x164.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Even though the submit button’s copy is very simple, it gets the point across and lets people know exactly what will happen when they click. This eliminates doubt about handing over an email address and boosts conversions.</p>\n<h3>3. Customize Your Forms</h3>\n<p>WPForms offers plenty of ways for you to customize your form, no matter what its purpose is:</p>\n<ul>\n<li>People prefer simple forms, and there’s no better way to create a clutter-free form than to <a href=\"https://wpforms.com/how-to-create-clutter-free-forms-with-smart-logic/\">use smart conditional logic</a></li>\n<li>Make it easier to collect information by giving users a way to <a href=\"https://wpforms.com/how-to-create-a-file-upload-form-in-wordpress/\">upload files to their form submissions</a></li>\n<li><a href=\"https://wpforms.com/addons/surveys-and-polls-addon/\">Create a survey or poll</a> and give users an interactive way to submit the information you want</li>\n</ul>\n<p><img class=\"alignnone size-full wp-image-177126\" src=\"https://wpforms.com/wp-content/uploads/2018/04/Image-Choices-to-Form-Fields.png\" alt=\"Image Choices to Form Fields\" width=\"562\" height=\"373\" srcset=\"https://wpforms.com/wp-content/uploads/2018/04/Image-Choices-to-Form-Fields.png 562w, https://wpforms.com/wp-content/uploads/2018/04/Image-Choices-to-Form-Fields-300x199.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>And lastly, do what no other contact form plugin on the market does – <a href=\"https://wpforms.com/announcing-multiple-choice-images-one-click-disclaimers-and-more/\">add images to multiple choice fields</a> so you can add some visual appeal to your site’s forms and make them really stand out.</p>\n<h3>4. Add an Incentive</h3>\n<p>One of the best ways to design your site’s forms to convert is to add an incentive to it so people will want to fill it out and submit it.</p>\n<p>By <a href=\"http://optinmonster.com/9-lead-magnets-to-increase-subscribers/\" target=\"_blank\" rel=\"noopener\">adding a lead magnet</a> designed to get people to take action, whether that be subscribe or buy, you can easily increase form conversions and watch your email list grow.</p>\n<p><img class=\"alignnone size-full wp-image-177125\" src=\"https://wpforms.com/wp-content/uploads/2018/04/Content-Upgrade.png\" alt=\"Content Upgrade\" width=\"701\" height=\"465\" srcset=\"https://wpforms.com/wp-content/uploads/2018/04/Content-Upgrade.png 701w, https://wpforms.com/wp-content/uploads/2018/04/Content-Upgrade-300x199.png 300w\" sizes=\"(max-width: 701px) 100vw, 701px\" /></p>\n<p>Just make sure your lead magnet solves a problem for site visitors, promises something, is very specific, is easy to read, adds a lot of value to your subscriber, and is easily accessible.</p>\n<p>In addition, make sure the style of your content upgrade matches your site’s brand and form so it seems like it fits in with your form’s overall design.</p>\n<p>And most of all, make sure the content upgrade is exciting enough to get people to <strong>want to sign up</strong>.</p>\n<h3>5. Add Social Proof</h3>\n<p>Adding social proof to your online forms helps instill trust with those that are on the fence about clicking submit. This is especially true when it comes to handing over personal information such as an email address or financial data.</p>\n<p>For instance, <a href=\"https://blog.bufferapp.com/\" target=\"_blank\" rel=\"nofollow noopener\">Buffer</a> adds the number of people that have already subscribed as a way to entice others to sign up too. After all, if that many people have already subscribed, they must be doing something right.</p>\n<p><img class=\"alignnone size-full wp-image-167478\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building.png\" alt=\"Social Proof for Email List Building\" width=\"562\" height=\"167\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building-300x89.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You can also add other forms of social proof such as:</p>\n<ul>\n<li>Industry experts making recommendations</li>\n<li>Official stamps of approval</li>\n<li>Friends like you</li>\n<li>Current user and customer reviews</li>\n<li>Celebrity endorsements</li>\n</ul>\n<p>People love to rely on the experiences and opinions of others. If you have positive sentiments you can share with new site visitors, use them to your advantage to boost conversions, no matter what kind of form you have on your website.</p>\n<h3>6. Provide Payment Options</h3>\n<p>If you have an online order form on your website, and collect payments from customers right on your form, it’s important to give people choice when it comes to how they’re going to pay.</p>\n<p>This is easily done by designing your form to have more than one payment option, so people don’t feel trapped into paying using a specific payment gateway.</p>\n<p>WPForms integrates with both <a href=\"https://wpforms.com/addons/paypal-standard-addon/\">PayPal</a> or <a href=\"https://wpforms.com/addons/stripe-addon/\">Stripe</a>, two of the most popular and widely recognized payment gateways around.</p>\n<p>Not to mention, WPForms also <a href=\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/\">supports recurring Stripe payments</a>, making it easy for you to configure your nonprofit donation, membership, subscription, and other forms to accept recurring donations.</p>\n<p><img class=\"alignnone size-full wp-image-305189\" src=\"https://wpforms.com/wp-content/uploads/2018/11/customized-donation-form.png\" alt=\"customized donation form\" width=\"562\" height=\"473\" /></p>\n<p>In addition, you can <a href=\"https://wpforms.com/how-to-let-customers-choose-between-check-or-paypal-payments/\">give customers the option to pay via check</a> for their orders by customizing your order form to have payment options for customers to pick from.</p>\n<p><img class=\"alignnone size-full wp-image-196259\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Payment-Options.png\" alt=\"Payment Options\" width=\"562\" height=\"170\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Payment-Options.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Payment-Options-300x91.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Giving people a chance to decide how they feel most comfortable paying will help increase conversions and customer satisfaction levels.</p>\n<h3>7. Reduce Friction</h3>\n<p>Any time a site visitor gets stuck on your WordPress form, there’s a chance they’ll abandon it before clicking submit.</p>\n<p>Luckily, there are some easy ways to design your form to reduce this friction and encourage people to finish filling out your form:</p>\n<ul>\n<li>Ask simple, to the point questions that people understand</li>\n<li>Place the simplest form fields at the beginning of your form so people get off to a good start</li>\n<li>Only add the form fields you absolutely need to process the form</li>\n<li>Enable auto-fill and auto-formatting functionality</li>\n<li>Allow copying and pasting of information into form fields</li>\n<li>Make it clear when a form field is not required using an asterisk or something similar</li>\n<li>Don’t force people to tab to multiple form fields if not necessary, like when they fill out their phone number</li>\n</ul>\n<p>Making your forms as easy as possible to fill out is the key to getting people from start to finish without abandoning.</p>\n<h3>8. Add a Progress Bar</h3>\n<p>Sometimes creating a long, multi-part form is necessary. If you want to avoid form fatigue and encourage more form conversions, consider <a href=\"https://wpforms.com/docs/how-to-create-multi-page-forms-in-wpforms/\">adding a customized progress bar</a> to forms that require multiple steps.</p>\n<p><img class=\"alignnone size-full wp-image-309663\" src=\"https://wpforms.com/wp-content/uploads/2018/11/progress-bar-example.png\" alt=\"progress bar example\" width=\"562\" height=\"194\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/progress-bar-example.png 562w, https://wpforms.com/wp-content/uploads/2018/11/progress-bar-example-300x104.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Need some design inspiration? Check out this <a href=\"https://wpforms.com/excellent-multi-page-form-examples-for-your-inspiration/\">roundup of excellent multi-page form examples</a> and create highly converting WordPress forms from now, even if they have to be longer than you’d like.</p>\n<h3>9. Avoid captchas</h3>\n<p>Protecting your forms from spam is crucial if you want to <a href=\"http://www.wpbeginner.com/wordpress-security/\" target=\"_blank\" rel=\"noopener\">keep your website secure</a>.</p>\n<p>In fact, here at WPForms we believe so strongly in spam prevention that we’ve not only included multiple spam protection features into our form builder, we’ve created an extensive guide helping you <a href=\"https://wpforms.com/how-to-build-spam-free-wordpress-contact-forms-the-ultimate-guide/\">create spam-free WordPress forms</a>.</p>\n<p>One of the most common ways to prevent web form spam is to enable captchas on your form that users must fill out before being able to submit their forms. But while this method can be very effective, it does cause some form abandonment because captchas can be tough to interpret and are often seen as intrusive to the user experience.</p>\n<p><img class=\"alignnone size-full wp-image-249921\" src=\"https://wpforms.com/wp-content/uploads/2018/08/CAPTCHA-Example.png\" alt=\"CAPTCHA Example\" width=\"562\" height=\"221\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/CAPTCHA-Example.png 562w, https://wpforms.com/wp-content/uploads/2018/08/CAPTCHA-Example-300x118.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you want to continue to protect your forms, but want to avoid ruining your form conversion rates, consider enabling WPForms’ Google invisible reCAPTCHA.</p>\n<p>This way, any time someone submits a form on your website, Google will determine whether the person submitting the form is a spambot or not. Your users however will never have to fill in a separate field to submit their form.</p>\n<h3>Bonus: Quick Design Tips for Boosting Form Conversions</h3>\n<p>Before we leave you to design your highly converting WordPress forms, we would like to share some a few more quick design tips for making your forms irresistible to site visitors:</p>\n<ol>\n<li>Avoid using Flash because it slows your site down which encourages form abandonment</li>\n<li>Don&#8217;t use popup forms on your mobile site</li>\n<li>Use a clear headline that states exactly what your form is about</li>\n<li><a href=\"https://wpforms.com/how-to-add-user-ratings-to-your-wordpress-forms-step-by-step/\">Add a rating system</a> to find out more of what your site visitors want</li>\n<li>Allow site visitors to <a href=\"https://wpforms.com/addons/signature-addon/\">leave their signature</a> so they can agree to your terms of service, sign a contract, or even sign a petition</li>\n</ol>\n<p>And there you have it! Some of the simple ways to design your forms to boost conversions.</p>\n<p>If you are looking to create some unique contact forms that involve CSS styling, check out our <a href=\"https://wpforms.com/beautiful-contact-form-designs-you-can-steal/\">six best examples</a> and get inspired today.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/simple-ways-to-design-your-forms-and-boost-conversions/\">10+ Simple Ways to Design Your Forms and Boost Conversions</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://wpforms.com/simple-ways-to-design-your-forms-and-boost-conversions/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"8 Insanely Easy Ways to Grow Your Email List Fast Starting Now\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:81:\"https://wpforms.com/insanely-easy-ways-to-grow-your-email-list-fast-starting-now/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:89:\"https://wpforms.com/insanely-easy-ways-to-grow-your-email-list-fast-starting-now/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 07 Nov 2018 13:00:01 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:9:\"Marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:12:\"email opt-in\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:15:\"email marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:14:\"subscribe form\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:13:\"list building\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=167473\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:580:\"<p>Do you want to grow your email list so you can reach more customers, generate more sales, and grow your business? Knowing the best email marketing strategies is the best way to build an email list. And fast. In this article, we’ll show you some of the easiest ways you can build your email list [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/insanely-easy-ways-to-grow-your-email-list-fast-starting-now/\">8 Insanely Easy Ways to Grow Your Email List Fast Starting Now</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:15720:\"<p>Do you want to grow your email list so you can reach more customers, generate more sales, and grow your business? Knowing the best email marketing strategies is the best way to build an email list. And fast.</p>\n<p>In this article, we’ll show you some of the easiest ways you can build your email list so you know any time you launch an email campaign, you’re reaching the widest audience possible.</p>\n<h3>1. Make Your Forms Simple</h3>\n<p>Sometimes the best way to get people to subscribe to your email list is to just ask them to.</p>\n<p>But you have to remember, people don’t want to deal with the hassle of filling out long, complicated forms, even if they want to subscribe.</p>\n<p>Don&#8217;t believe us? Check out this <a href=\"https://www.quicksprout.com/2013/01/31/how-to-optimize-contact-forms-for-conversions/?display=wide\" target=\"_blank\" rel=\"nofollow noopener\">infographic by Quick Sprout</a>:</p>\n<p><img class=\"alignnone size-full wp-image-167474\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Make-Forms-Simple.png\" alt=\"Make Forms Simple\" width=\"562\" height=\"210\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Make-Forms-Simple.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Make-Forms-Simple-300x112.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Notice that the more fields you make visitors fill out, the less likely to subscribe they are.</p>\n<p>That’s why using a forms plugin such as <a href=\"https://wpforms.com\">WPForms</a> is one of the best list building strategies. Not only is it beginner friendly, <strong>it’s user-friendly too</strong>.</p>\n<p>With WPForms, you can build your email list in two ways.</p>\n<ol>\n<li>Create a signup form specifically asking visitors to subscribe</li>\n<li><a href=\"https://wpforms.com/how-to-add-an-aweber-signup-checkbox-to-your-contact-form/\"> Create a contact form with a checkbox</a> allowing visitors to be added to your email list automatically after selecting it</li>\n</ol>\n<p>WPForms does more than integrate easily with <a href=\"http://www.wpbeginner.com/showcase/best-email-marketing-services/\" target=\"_blank\" rel=\"noopener\">popular email service providers</a> such as <a href=\"https://wpforms.com/addons/aweber-addon/\">AWeber</a>, <a href=\"https://wpforms.com/addons/mailchimp-addon/\">MailChimp</a>, <a href=\"https://wpforms.com/addons/getresponse-addon/\">GetResponse</a>, <a href=\"https://wpforms.com/addons/drip-addon/\">Drip</a>, <a href=\"https://wpforms.com/features/constant-contact/\">Constant Contact</a>, and <a href=\" https://wpforms.com/addons/campaign-monitor-addon/\">Campaign Monitor</a>.</p>\n<p>It also allows you to embed your signup form anywhere on you want on your WordPress site. Add it to your posts, pages, sidebar widgets, and even footer sections using shortcodes. This guarantees site visitors have the chance to subscribe no matter where they are on your website.</p>\n<p>Not to mention, you can create other cool forms that ask people to sign up like <a href=\"https://wpforms.com/how-to-create-a-simple-order-form-in-wordpress/\">order forms</a>, <a href=\"https://wpforms.com/how-to-create-a-custom-login-form-in-wordpress/\">login forms</a>, and even <a href=\"https://wpforms.com/how-to-create-a-non-profit-donation-form/\">donation forms</a>.</p>\n<h3>2. Optin Incentives</h3>\n<p>Giving people a solid reason to subscribe is one of the easiest ways to convert more subscribers.</p>\n<p>Offer optin incentives such as eBooks, swipe files, free software tools, access to exclusive online courses, white papers, and even resource lists and watch your email list grow.</p>\n<p>For instance, Neil Patel offers subscribers access to a free course on his <a href=\"https://www.quicksprout.com/blog/\" target=\"_blank\" rel=\"nofollow noopener\">Quick Sprout blog</a>, which is valued at $300.</p>\n<p><img class=\"alignnone size-full wp-image-167476\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Opt-In-Incentive-for-Capturing-Email-Addresses.png\" alt=\"Opt In Incentive for Capturing Email Addresses\" width=\"562\" height=\"386\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Opt-In-Incentive-for-Capturing-Email-Addresses.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Opt-In-Incentive-for-Capturing-Email-Addresses-300x206.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>This entices people to sign up because not only are they receiving something of high value for free, they don’t want to miss out on learning how to drive traffic to their website.</p>\n<p>The reason for using an optin incentive is twofold:</p>\n<ol>\n<li>Offer your incentive on any signup form on your site – sidebar forms, lead capture top bars, optin popups, blog post footers, and even author bios so all site visitors, no matter where they navigate on your site, see it</li>\n<li>You only need to come up with one really great incentive piece, since the same offer will be available on all subscribe forms</li>\n</ol>\n<p>Use this simple method for building a bigger email list and watch subscription rates increase.</p>\n<h3>3. Enable Popups</h3>\n<p>Using a lead generation solution such as <a href=\"https://optinmonster.com/\" target=\"_blank\" rel=\"noopener\">OptinMonster</a> is another quick and easy way to boost your site’s conversion rates. Especially if you want to create cool looking popups on your site.</p>\n<p>Better yet, take advantage of <a href=\"https://optinmonster.com/features/exit-intent/\" target=\"_blank\" rel=\"noopener\">OptinMonster’s Exit-Intent® technology</a> and target visitors as they are about to leave your site.</p>\n<p>This gives them one last chance to consider subscribing before they leave for what may be forever. Make it even more enticing by adding an optin incentive such as the ones mentioned above.</p>\n<p><img class=\"alignnone size-full wp-image-167477\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Exit-Intent-Technology-Popup-Example.png\" alt=\"Exit Intent Technology Popup Example\" width=\"562\" height=\"337\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Exit-Intent-Technology-Popup-Example.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Exit-Intent-Technology-Popup-Example-300x180.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>OptinMonster differs from WPForms in that it offers multiple form types such as lightbox popups, slide-ins, sidebar forms, floating bars, and more.</p>\n<p>This means you can have your pick when it comes to choosing which type of subscribe form you want to add to your site.</p>\n<p>In addition, you can A/B test your subscribe forms, access built-in form analytics to <a href=\"http://optinmonster.com/beginners-guide-to-email-marketing/\" target=\"_blank\" rel=\"noopener\">make sure your email list building efforts are working</a>, and enable page-level targeting so the right forms appear before the right audience.</p>\n<p>You can also use <a href=\"https://optinmonster.com/features/monsterlinks/\" target=\"_blank\" rel=\"noopener\">creatively placed MonsterLinks</a>, that when clicked, enable a popup to appear.</p>\n<p>Try this technique and you might boost conversion rates as much as <a href=\"https://optinmonster.com/active-lifestyle-brand-marketing-case-study/\" target=\"_blank\" rel=\"noopener\">Sportique.com did</a> &#8211; a whopping 58% throughout their site, and an even more impressive <strong>79% on the cart page alone</strong>.</p>\n<p>If this sounds like something you want to do, check out <a href=\"http://optinmonster.com/features/\" target=\"_blank\" rel=\"noopener\">OptinMonster’s full feature list</a>.</p>\n<h3>4. Leverage Social Proof</h3>\n<p>People love to make decisions on whether to subscribe based on their initial impressions.</p>\n<p>That’s why using social proof on your subscribe forms is a great way to give your site visitors the perfect first impression and convince them to subscribe right away.</p>\n<p>There are six types of social proof you can use:</p>\n<ol>\n<li>Industry experts making recommendations</li>\n<li>Celebrity endorsements</li>\n<li>Current user and customer reviews</li>\n<li>Wisdom of crowds</li>\n<li>People like you sharing on social media and other platforms</li>\n<li>Official stamps of approval</li>\n</ol>\n<p>You can add any of these types of social proof to your subscribe forms, but one of the most commonly used is wisdom of crowds. Using the sheer power of numbers to show how reliable you are is a great way to prove to new site visitors that subscribing is worth it.</p>\n<p>For example, <a href=\"https://blog.bufferapp.com/\" target=\"_blank\" rel=\"nofollow noopener\">Buffer</a> adds how many subscribers you would be joining if you too subscribe to their email list.</p>\n<p><img class=\"alignnone size-full wp-image-167478\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building.png\" alt=\"Social Proof for Email List Building\" width=\"562\" height=\"167\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Social-Proof-for-Email-List-Building-300x89.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>After all, if hundreds of thousands of people have already handed over their email address, it’s safe to say joining the ranks is a good idea.</p>\n<h3>5. Lock Your Content</h3>\n<p>Gated content is content such as articles, white papers, videos, etc. that can only be accessed by site visitors that have filled out your WordPress form and provided an email address.</p>\n<p>For instance, <a href=\"http://www2.pardot.com/sem/us/pardot_salesforce\" target=\"_blank\" rel=\"nofollow noopener\">Pardot</a> does a great job of gating a white paper. They preview what&#8217;s in the white paper, which generates interest, and then require you to subscribe to see the real deal.</p>\n<p><img class=\"alignnone size-full wp-image-308962\" src=\"https://wpforms.com/wp-content/uploads/2018/11/gated-content.png\" alt=\"gated content\" width=\"562\" height=\"446\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/gated-content.png 562w, https://wpforms.com/wp-content/uploads/2018/11/gated-content-300x238.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Gated content makes growing your email list super simple because you&#8217;re using content you’ve already published, as opposed to lead magnets or content upgrades, which take time to create and have to be changed or updated from time to time.</p>\n<h3>6. Create a Quiz</h3>\n<p>There’s no denying that killer blog content reigns supreme when it comes to building a large following, generating more sales, and establishing a solid reputation.</p>\n<p>But <strong>interactive content</strong> can be a whole lot of fun for people too, and happens to be a great way to gather more email addresses from site visitors.</p>\n<p>In fact, a CMI Survey found that:</p>\n<ul>\n<li>70% of marketers agree that interactive content is effective at converting site visitors</li>\n<li>88% of marketers believe that interactive content is a good way to differentiate themselves from the competition</li>\n<li>81% of people think that interactive content is better at grabbing people’s attention than static content</li>\n</ul>\n<p>And that’s just the beginning.</p>\n<p>If you want a unique way to get people to subscribe to your email list, that’s also pretty simple, do what the <a href=\"http://fyia.worldwildlife.org/\" target=\"_blank\" rel=\"nofollow noopener\">World Wildlife Fund</a> does and create a quiz that requires people to subscribe to take.</p>\n<p><img class=\"alignnone size-full wp-image-308963\" src=\"https://wpforms.com/wp-content/uploads/2018/11/quiz-example.png\" alt=\"quiz example\" width=\"562\" height=\"517\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/quiz-example.png 562w, https://wpforms.com/wp-content/uploads/2018/11/quiz-example-300x276.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you prefer, because you feel asking for an email before taking the quiz may put some people off, ask for an email after site visitors are done taking a quiz in order to get results.</p>\n<h3>7. Interact on Forums</h3>\n<p>You might frequent forums like Quora on a regular basis to find out what your target audience is talking about, to generate blog content ideas, and to promote your brand.</p>\n<p>But did you know you can also interact on forums like that to build your email list?</p>\n<p><img class=\"alignnone size-full wp-image-308961\" src=\"https://wpforms.com/wp-content/uploads/2018/11/forum-example.png\" alt=\"forum example\" width=\"562\" height=\"651\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/forum-example.png 562w, https://wpforms.com/wp-content/uploads/2018/11/forum-example-259x300.png 259w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Try answering questions on popular forums, leaving a link to your site’s landing page that has a WPForms newsletter signup form on it, and getting people to subscribe to find out more on the topic that was answered in the forum.</p>\n<p>This strategy works because you&#8217;re showing people in forums that you are real and care about what people are taklking about. It also drives people to your website that you know are already interested in what you have to talk about, making your chances of converting visitors into subscribers more likely.</p>\n<p>You can also use forums to find great ideas for lead magnets that you add to your subscribe forms. After all, if you know firsthand what people are wondering about, you can use that information to create a lead magnet.</p>\n<h3>8. Instill Trust</h3>\n<p>People fail to fill out your WordPress forms for many reasons. For example, they worry about spam, misuse of their personal information, and hackers stealing their data.</p>\n<p>Luckily, WPForms offers a few ways for you to assure people that by subscribing to your email list their information is safe and that you&#8217;ll use it responsibility:</p>\n<ul>\n<li>Add an anti-spam statement to your subscribe form such as “Your email address is 100% secure and we will never sell your information to a 3rd party”</li>\n<li><a href=\"https://wpforms.com/how-to-add-a-gdpr-agreement-field-to-your-sites-forms/\">Add a GDPR form field</a> so you can get consent from site visitors to collect and store their personal information, while also showing your visitors you care about how their information will be used</li>\n</ul>\n<p><img class=\"alignnone size-full wp-image-207969\" src=\"https://wpforms.com/wp-content/uploads/2018/05/GDPR-Form-Field.gif\" alt=\"GDPR Form Field\" width=\"868\" height=\"626\" /></p>\n<p>You can also make it clear and easy for people to unsubscribe if at any time they want to, so people never feel trapped and don’t feel the need to mark your emails as spam.</p>\n<p>And that’s it for today! You now have four super easy ways to grow your email list starting now.</p>\n<p>If you are looking for ways to optimize your mobile device lead generation strategies, check out <a href=\"https://wpforms.com/mobile-lead-generation/\">how to optimize your forms for your mobile audience</a>.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>If you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/insanely-easy-ways-to-grow-your-email-list-fast-starting-now/\">8 Insanely Easy Ways to Grow Your Email List Fast Starting Now</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://wpforms.com/insanely-easy-ways-to-grow-your-email-list-fast-starting-now/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"5 Effective Ways to Sell Ad Space on Your WordPress Website\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:78:\"https://wpforms.com/effective-ways-to-sell-ad-space-on-your-wordpress-website/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://wpforms.com/effective-ways-to-sell-ad-space-on-your-wordpress-website/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 06 Nov 2018 13:00:29 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:9:\"Marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:21:\"multiple file uploads\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:17:\"advertise with us\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:17:\"make money online\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:7:\"WPForms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=308071\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:570:\"<p>Do you need some effective ways to sell ad space on your WordPress website? Monetizing your website by selling ads is a great way to make money online and supplement your monthly income. In this article, we’ll share with you some of the best ways to sell ad space on your website so you can [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/effective-ways-to-sell-ad-space-on-your-wordpress-website/\">5 Effective Ways to Sell Ad Space on Your WordPress Website</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:12884:\"<p>Do you need some effective ways to sell ad space on your WordPress website? Monetizing your website by selling ads is a great way to <a href=\"https://www.wpbeginner.com/beginners-guide/make-money-online/\" target=\"_blank\" rel=\"noopener\">make money online</a> and supplement your monthly income.</p>\n<p>In this article, we’ll share with you some of the best ways to sell ad space on your website so you can begin to earn passive income.</p>\n<h3>What is Needed to Sell Ad Space on Your Website?</h3>\n<p><img class=\"alignnone size-full wp-image-308075\" src=\"https://wpforms.com/wp-content/uploads/2018/11/online-advertisements.jpg\" alt=\"online advertisements\" width=\"562\" height=\"322\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/online-advertisements.jpg 562w, https://wpforms.com/wp-content/uploads/2018/11/online-advertisements-300x172.jpg 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You might be thinking that having a website is all you need to sell ad space on your website to other businesses.</p>\n<p>While that’s technically true, there’s a lot more to monetizing your website with ad space than simply filling your webpages up with random ads.</p>\n<p>After all, if no one buys the products and services you promote on your website, businesses are not going to continue paying you for ad space.</p>\n<p>Luckily, we know what some of the most important requirements are for being ready to sell ad space on your WordPress website:</p>\n<ul>\n<li><strong>Your Own Website:</strong> this may seem obvious, but if you want to make money selling ad space, you have to have your own website. For help with this, check out this useful tutorial on <a href=\"http://www.wpbeginner.com/start-a-wordpress-blog/\" target=\"_blank\" rel=\"noopener\">how to start a WordPress blog to get started</a>.</li>\n<li><strong>Site Traffic:</strong> there is no way anyone is going to pay you to advertise their products on your website if there isn’t sufficient traffic coming to your site every day.</li>\n<li><strong>Quality Blog Content:</strong> businesses wanting to promote their products and services on your website want to tap into the traffic that comes to your site every day. They also want to know that the people that come to your site don’t leave right away because you aren’t offering high-quality, valuable blog content.</li>\n<li><strong>Design and Functionality:</strong> Your site visitors should be able to navigate your website easily. This increases the exposure they’ll have to the different ads you have displaying on your site.</li>\n</ul>\n<p>Now that you know the basics needed to successfully sell ad space on your website, let’s take a look at some tips for selling the actual space on your website.</p>\n<h3>1. Use a WordPress Form Builder and Sell Ad Space Directly</h3>\n<p>Let’s face it, sometimes you need to blatantly advertise that you have ad space available, especially if you’re just starting out.</p>\n<p>But that’s okay because the <a href=\"https://wpforms.com\">WPForms plugin</a> has you covered when it comes to telling people that you have retail space on your site for advertisements.</p>\n<p>Selling ads directly to business owners is beneficial for a number of reasons:</p>\n<ul>\n<li>You have full control over pricing</li>\n<li>Communicating with advertisers is easier since there is no middleman</li>\n<li>You can show interested advertisers your site&#8217;s metrics</li>\n<li>You get to keep all revenue generated since there is no third-party company involvement</li>\n<li>Offering discounts to loyal advertisers is a cinch, which can foster long-term relationships and guaranteed passive income</li>\n</ul>\n<p>WPForms has an exclusive Advertise with Us form template that makes selling ad space on your website simple.</p>\n<p>In fact, those with interest just have to fill out your form that’s fully customized by you.</p>\n<p><img class=\"alignnone size-full wp-image-258395\" src=\"https://wpforms.com/wp-content/uploads/2018/08/advertise-with-us-form-fields.png\" alt=\"advertise with us form fields\" width=\"562\" height=\"518\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/advertise-with-us-form-fields.png 562w, https://wpforms.com/wp-content/uploads/2018/08/advertise-with-us-form-fields-300x277.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you want to collect payments from people on your forms, rest assured WPForms integrates with both <a href=\"https://wpforms.com/addons/paypal-standard-addon/\">PayPal</a> and <a href=\"https://wpforms.com/addons/stripe-addon/\">Stripe</a> so you can easily collect money.</p>\n<p>For help with selling ad space on your website using an online form, check out our tutorial on <a href=\"https://wpforms.com/how-to-create-an-advertise-with-us-form-in-wordpress-updated/\">how to create an advertise with us form in WordPress</a>.</p>\n<h3>2. Approach Advertisers Directly</h3>\n<p>If you really want to dive in and do some work, there’s always the option of approaching advertisers directly and seeing if they’re interested in buying ad space that’s available on your website.</p>\n<p>While this may be tough to do, if you have an industry specific website and a lot of traffic, you’ll be able to convince some businesses to work with you without a problem.</p>\n<p>Here are some tips from approaching potential advertisers directly:</p>\n<ul>\n<li>Use Google Analytics to help you discover more about your site visitors so you know what kind of businesses they’ll likely want to buy from</li>\n<li>Research your competition and see who advertises on their site; from there you can either contact those same businesses or those with similar products and services for sale</li>\n<li>Be ready to share why your site will be of interest to potential customers of theirs, what your bounce rate, average time on site, and pageview stats look like, and how previous advertisers have done when displaying ads on your website</li>\n<li>Track the number of outbound links your site sees on a regular basis and share that with possible advertisers to show your site visitors’ willingness to click away from your site and onto something else that interests them</li>\n<li>Show advertisers how you plan to promote your website to your existing followers and a broader audience base</li>\n</ul>\n<p>If you can convince businesses selling high-quality products and services your site visitors will love to display ads on your website, you’re sure to make a lot of money with very little effort.</p>\n<h3>3. Make it Automatic</h3>\n<p>If you don’t want to deal with the hassle of finding businesses that want to pay you to display ads on your website, or just don’t know where to start, a good thing to try first is automating the process.</p>\n<p>For example, you can use a service like <a href=\"https://www.google.com/adsense/start/#/?modal_active=none\" target=\"_blank\" rel=\"nofollow noopener\">AdSense</a> to monetize your available ad space and easily generate extra cash through your site visitors:</p>\n<ul>\n<li>Pick from text, text and image, linked, and even video ads</li>\n<li>Select where on your site to display each ad type</li>\n<li>Advertisers that want you to display their ad on your website then bid for the prime ad space available</li>\n<li>Every time someone sees or clicks an ad, you make money</li>\n</ul>\n<p>Check out how <a href=\"https://www.wikihow.com/Recycle-Running-Shoes\" target=\"_blank\" rel=\"nofollow noopener\">wikiHow</a>, one of the original users of AdSense displays ads on their website:</p>\n<p><img class=\"alignnone size-full wp-image-308073\" src=\"https://wpforms.com/wp-content/uploads/2018/11/adsense-example.png\" alt=\"adsense example\" width=\"562\" height=\"466\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/adsense-example.png 562w, https://wpforms.com/wp-content/uploads/2018/11/adsense-example-300x249.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Notice that in an article for recycling running shoes an ad for brand spanking new Nike running shoes displays in not one, but two sections.</p>\n<p>The great thing about automatic advertising is that you don’t have to do a lot of work. Using special algorithms, these automated ad services can detect relevant ads that should display for your site visitors based on the type of content you publish, so nothing truly seems out of place and your site visitors are more likely to be interested and click through.</p>\n<h3>3. Test the Big Three and See Which One(s) Make the Most Money</h3>\n<p>There are three popular advertisement types you can sell to businesses looking to drive traffic to their websites by placing ads on your website:</p>\n<ol>\n<li><strong>Display Advertisements:</strong> these are bought and sold between you and the business directly. They will generate you money based on how many impressions they receive. This means the more people that come to your website and see the ads displayed on your site, the more money you make.</li>\n<li><strong>Banner Advertisements:</strong> this method works by selling actually space on your website where ads will display. The more highly trafficked the webpage, the pricier the piece of retail space. You can display many different size banner ads.</li>\n<li><strong>Popup Advertisements:</strong> these ads display as popups to all site visitors that land on your site. They can be annoying to site visitors though and cause your loyal following to decrease, especially if the ads are not relevant.</li>\n</ol>\n<p>You’ll never know which one of the big three will generate you the most money unless you experiment with them all.</p>\n<h3>4. Consider Placement of Ads Carefully</h3>\n<p><img class=\"alignnone size-full wp-image-308072\" src=\"https://wpforms.com/wp-content/uploads/2018/11/ad-placement.jpg\" alt=\"ad placement\" width=\"562\" height=\"322\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/ad-placement.jpg 562w, https://wpforms.com/wp-content/uploads/2018/11/ad-placement-300x172.jpg 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>As we mentioned above, you’ll never know which type of ad will resonate the most with your site visitors.</p>\n<p>That said, there are some ad positions that are known to generate more money than others: within site content, in the whitespace of your website, and in the navigational menu.</p>\n<p>If you aren’t sure which placement is working best on your website, and you happen to use AdSense to automate the process for you, check out <a href=\"https://www.monsterinsights.com/\" target=\"_blank\" rel=\"noopener\">MonsterInsights</a>, the best Google Analytics plugin on the market to date.</p>\n<p>MonsterInsights seamlessly connects your WordPress site to Google Analytics so you can easily see where site visitors are coming from, who they are, and what they do once on your website <strong>right in your WordPress dashboard</strong>.</p>\n<p>It also helps you track AdSense ads so you take advantage of the highest earning ad spaces on your website.</p>\n<p><img class=\"alignnone size-full wp-image-308074\" src=\"https://wpforms.com/wp-content/uploads/2018/11/monsterinsights-ad-tracking.png\" alt=\"monsterinsights ad tracking\" width=\"562\" height=\"345\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/monsterinsights-ad-tracking.png 562w, https://wpforms.com/wp-content/uploads/2018/11/monsterinsights-ad-tracking-300x184.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>This comes in handy when selling ad space to advertisers directly. With cold hard facts about how ads performs on your website, you’ll not only be able to convince more people to buy, but demand higher prices for you ad space too.</p>\n<p>And there you have it! You now know some of the most effective ways to sell ad space on your WordPress website so you can generate supplemental income or make a fulltime living out of advertising on your popular website.</p>\n<p>Do you want people to upload images on your advertise with us forms so you can get a better idea of what their intentions are? If so, check out <a href=\"https://wpforms.com/how-to-create-wordpress-contact-form-multiple-file-upload/\">how to create a WordPress form with multiple file uploads</a> and start monetizing your site today.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/effective-ways-to-sell-ad-space-on-your-wordpress-website/\">5 Effective Ways to Sell Ad Space on Your WordPress Website</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:83:\"https://wpforms.com/effective-ways-to-sell-ad-space-on-your-wordpress-website/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:55:\"How to Create a Drip Signup Form in WordPress (Updated)\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://wpforms.com/how-to-create-a-drip-signup-form-in-wordpress-updated/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:82:\"https://wpforms.com/how-to-create-a-drip-signup-form-in-wordpress-updated/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 05 Nov 2018 13:00:26 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:6:{i:0;a:5:{s:4:\"data\";s:19:\"WordPress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:15:\"lead generation\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:15:\"email marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:11:\"conversions\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:4:\"drip\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:10:\"drip addon\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=154726\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:570:\"<p>Do you want an easy way to create a Drip signup form in WordPress? WPForms allows you to quickly connect your WordPress forms with Drip so you can automate the process of collecting leads and building a bigger email list. In this article, we’ll show you the most efficient way to create a Drip form [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-create-a-drip-signup-form-in-wordpress-updated/\">How to Create a Drip Signup Form in WordPress (Updated)</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:20041:\"<p>Do you want an easy way to create a Drip signup form in WordPress? WPForms allows you to quickly connect your WordPress forms with Drip so you can automate the process of collecting leads and building a bigger email list.</p>\n<p>In this article, we’ll show you the most efficient way to create a Drip form in WordPress.</p>\n<h3>Step 1: Create a Drip Signup Form in WordPress</h3>\n<p>The first thing you’ll need to do is install and activate the <a href=\"https://wpforms.com/pricing/\">WPForms plugin</a>. For more details, see this step by step guide on <a title=\"Step by step guide on How to Install a WordPress Plugin\" href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\">how to install a plugin in WordPress</a>.</p>\n<p>Next, go to <strong>WPForms » Addons</strong> and find the <strong>Drip Addon</strong>.</p>\n<p>Install and activate the Drip addon.</p>\n<p><img class=\"alignnone size-full wp-image-307516\" src=\"https://wpforms.com/wp-content/uploads/2018/02/wpforms-drip-addon.png\" alt=\"wpforms drip addon\" width=\"562\" height=\"211\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/wpforms-drip-addon.png 562w, https://wpforms.com/wp-content/uploads/2018/02/wpforms-drip-addon-300x113.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Once your WPForms Drip addon is active, go to <strong>WPForms » Add New</strong> to create a new form.</p>\n<p>On the setup screen, name your form and select the Newsletter Signup Form template.</p>\n<p><img class=\"alignnone size-full wp-image-207967\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Template.png\" alt=\"Newsletter Signup Form Template\" width=\"562\" height=\"318\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Template.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Template-300x170.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>WPForms will create a simple newsletter signup form.</p>\n<p>Here, you can add additional fields by dragging them from the left hand panel to the right hand panel. Then, click on the field to make any changes. You can also click on a form field and drag it to rearrange the order on your Drip subscribe form.</p>\n<p><img class=\"alignnone size-full wp-image-207966\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview.png\" alt=\"Newsletter Signup Form Preview\" width=\"562\" height=\"348\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview-300x186.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>One important form field to consider adding to your Drip signup form is the <a href=\"https://wpforms.com/how-to-add-a-gdpr-agreement-field-to-your-sites-forms/\">GDPR Agreement field</a>.</p>\n<p>This form field helps you <a href=\"https://wpforms.com/docs/how-to-create-gdpr-compliant-forms/\">create GDPR compliant forms</a> by giving your site visitors an explicit way to agree to you storing their personal information and sending them email marketing materials.</p>\n<p><img class=\"alignnone size-full wp-image-207969\" src=\"https://wpforms.com/wp-content/uploads/2018/05/GDPR-Form-Field.gif\" alt=\"GDPR Form Field\" width=\"868\" height=\"626\" /></p>\n<p>To change the default language of the agreement field, all you have to do is click on the field to make changes.</p>\n<p>If you do add the GDPR Agreement field to your subscribe form, you’ll need to go to <strong>WPForms</strong> » <strong>Settings</strong> and click on the <strong>GDPR Enhancements</strong> checkbox.</p>\n<p><img class=\"alignnone size-full wp-image-207959\" src=\"https://wpforms.com/wp-content/uploads/2018/05/GDPR-Enhancement-Features-2.png\" alt=\"GDPR Enhancement Features\" width=\"562\" height=\"304\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/GDPR-Enhancement-Features-2.png 562w, https://wpforms.com/wp-content/uploads/2018/05/GDPR-Enhancement-Features-2-300x162.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>When you do this, you’ll enable the GDPR enhancement features WPForms has available for you to make complying with GDPR that much easier.</p>\n<p>In fact, you’ll be able to:</p>\n<ul>\n<li>Disable user cookies</li>\n<li>Disable user details, which includes IP addresses and User Agent data</li>\n<li>Access the GDPR form field for your WordPress forms</li>\n</ul>\n<p>When your form looks the way you want it to, click <strong>Save</strong>.</p>\n<p>Now, we’ll take a look at how to configure your form’s settings so you can connect your signup form to your newsletter service.</p>\n<h3>Step 2: Connect Your Form to Drip</h3>\n<p>To connect your newsletter signup form to Drip, go to <strong>Marketing » Drip</strong> in the Form Editor.</p>\n<p><img class=\"alignnone size-full wp-image-307514\" src=\"https://wpforms.com/wp-content/uploads/2018/02/drip-marketing-settings.png\" alt=\"drip marketing settings\" width=\"562\" height=\"399\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/drip-marketing-settings.png 562w, https://wpforms.com/wp-content/uploads/2018/02/drip-marketing-settings-300x213.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, click <strong>Add New Account</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-307509\" src=\"https://wpforms.com/wp-content/uploads/2018/02/add-new-drip-account.png\" alt=\"add new drip account\" width=\"562\" height=\"200\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/add-new-drip-account.png 562w, https://wpforms.com/wp-content/uploads/2018/02/add-new-drip-account-300x107.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>A box will pop up asking your for your Drip account name, which can be any name you like. You’ll also need to enter your Drip API Key, which can be found in your Drip account.</p>\n<p><img class=\"alignnone size-full wp-image-307515\" src=\"https://wpforms.com/wp-content/uploads/2018/02/new-drip-account.png\" alt=\"new drip account\" width=\"562\" height=\"458\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/new-drip-account.png 562w, https://wpforms.com/wp-content/uploads/2018/02/new-drip-account-300x244.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, a modal will popup asking you to name the connection, which again you can name anything you want since it’s for your reference only.</p>\n<p><img class=\"alignnone size-full wp-image-307513\" src=\"https://wpforms.com/wp-content/uploads/2018/02/drip-connection-nickname.png\" alt=\"drip connection nickname\" width=\"562\" height=\"339\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/drip-connection-nickname.png 562w, https://wpforms.com/wp-content/uploads/2018/02/drip-connection-nickname-300x181.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Click <strong>OK</strong>.</p>\n<p>On the next screen, you’ll need to connect Drip to WPForms.</p>\n<p>First, choose your Drip account from the dropdown menu.</p>\n<p><img class=\"alignnone size-full wp-image-307510\" src=\"https://wpforms.com/wp-content/uploads/2018/02/drip-account-option.png\" alt=\"drip account option\" width=\"562\" height=\"308\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/drip-account-option.png 562w, https://wpforms.com/wp-content/uploads/2018/02/drip-account-option-300x164.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, you’ll need to choose an <strong>Action to Perform</strong>:</p>\n<ul>\n<li>Subscriber: Create or Update</li>\n<li>Subscriber: Delete</li>\n<li>Campaign: Subscribe</li>\n<li>Campaign: Unsubscribe</li>\n<li>Record Event</li>\n</ul>\n<p>These actions can be grouped into two categories: times you want to add/update a Drip subscriber and times you want to delete/unsubscribe a Drip subscriber.</p>\n<p>Let’s take a quick look at each.</p>\n<p><strong>Actions to Add/Update a Drip Subscriber</strong><br />\nAdding subscribers to your Drip email list is most likely you goal when creating a Drip signup form on your website.</p>\n<p>That’s why it’s likely you’ll choose one of the following actions when connecting Drip to WPForms:</p>\n<ul>\n<li><strong>Subscriber: Create or Update.</strong> This is for adding new subscribers to your Drip email list. You can also update an existing subscriber’s information. This does not automatically add any subscribers to any campaigns you’ve created.</li>\n<li><strong>Campaign: Subscribe.</strong> This will not only add a new subscriber to your Drip email list, it will automatically add them to a campaign you’ve created in your Drip account.</li>\n<li><strong>Record Event.</strong> This will add a new subscriber to your Drip email list and add an Event to them, which is used to track the activity of your subscribers.</li>\n</ul>\n<p><strong>Actions to Delete/Unsubscribe a Drip Subscriber</strong><br />\nYou might have a form on your website that allows current subscribers to remove themselves from your Drip email list. If that’s the case, you’ll choose from one of the following actions:</p>\n<ul>\n<li><strong>Subscriber: Delete.</strong>. This will allow your subscriber to remove themselves from your Drip email list.</li>\n<li><strong>Campaign: Unsubscribe.</strong> This action will give subscribers a chance to remove themselves from one or all campaigns they are a part of, but they will still be on your Drip email list.</li>\n</ul>\n<p>For our example, we’ll choose the <strong>Subscriber: Create or Update</strong> option, which after chosen from the dropdown menu, will open a new set of settings to configure.</p>\n<p><img class=\"alignnone size-full wp-image-307511\" src=\"https://wpforms.com/wp-content/uploads/2018/02/drip-action-settings.png\" alt=\"drip action settings\" width=\"562\" height=\"656\" srcset=\"https://wpforms.com/wp-content/uploads/2018/02/drip-action-settings.png 562w, https://wpforms.com/wp-content/uploads/2018/02/drip-action-settings-257x300.png 257w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Here’s a brief look at those settings:</p>\n<ul>\n<li><strong>Subscriber Email</strong> is for new subscribers or those wanting to update their emails</li>\n<li><strong>New Subscriber Email</strong> is the new email when an existing subscriber updates their email</li>\n<li><strong>Send User ID to Drip (if the user was logged in)</strong> this will match the user’s WordPress ID to the user ID Drip assigns all subscribers</li>\n<li><strong>Send user IP address to Drip</strong> this will send your subscribers’ IP addresses to your Drip account</li>\n<li><strong>Mark user as a prospect</strong> to flag subscribers you can easily tag them as leads within Drip</li>\n<li><strong>Starting Lead Score Value</strong> if you select the option <strong>Mark user as a prospect</strong> the user will be assigned a lead score indicating their level of engagement.</li>\n<li><strong>Tags to Add/Delete</strong> identify subscribers so you can segment them and send targeted email campaigns</li>\n</ul>\n<p>Once you’ve chosen all the settings you want to configure, click <strong>Save</strong>.</p>\n<h3>Step 3: Configure Your Subscribe Form Settings</h3>\n<p>To configure the rest of your Drip signup forms’ settings, go to <strong>Settings</strong> » <strong>General</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-246850\" src=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings.png\" alt=\"WPForms General Settings\" width=\"562\" height=\"443\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings-300x236.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Here you can configure the following:</p>\n<ul>\n<li><strong>Form Name.</strong> Rename your form here.</li>\n<li><strong>Form Description.</strong> Give your form a description.</li>\n<li><strong>Submit Button Text.</strong> Customize the copy on the submit button.</li>\n<li><strong>Spam Prevention.</strong> <a href=\"https://wpforms.com/how-to-build-a-spam-free-contact-form-without-captcha/\">Enable the anti-spam honeypot feature</a> to prevent spambot submissions. This feature is automatically enabled on all WordPress forms. If you decide to use another form of spam prevention, unclick this option.</li>\n<li><strong>GDPR Enhancements.</strong> If you enabled the GDPR enhancements in step 1, here is where you can disable the storing of entry information and user details, such as IP addresses and user agents, in an effort to comply with GDPR requirements.</li>\n</ul>\n<p>When you’re done, click <strong>Save</strong>.</p>\n<h3>Step 4: Configure Your Subscribe Form’s Notifications</h3>\n<p>Notifications are a great way to send an email whenever a form is submitted on your website.</p>\n<p><img class=\"alignnone size-full wp-image-246852\" src=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings.png\" alt=\"WPForms Notifications Settings\" width=\"562\" height=\"228\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings-300x122.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Unless you disable this feature, whenever someone submits a subscribe form on your site, you’ll get a notification about the submission.</p>\n<p>If you <a href=\"https://wpforms.com/docs/how-to-use-smart-tags-in-wpforms/\">use smart tags</a>, you can also send a notification to the user when they subscribe, letting them know you received it and will be in contact shortly. This assures site visitors their form has gone through properly.</p>\n<p>For help with this step, check out our documentation on <a href=\"https://wpforms.com/docs/setup-form-notification-wpforms/\">how to set up form notifications in WordPress</a>.</p>\n<p>Adding to this, if you want to keep branding across your emails consistent, you can check out this guide on <a href=\"https://wpforms.com/docs/how-to-add-custom-header-image-in-your-wpforms-email-template/\">adding a custom header to your email template</a>.</p>\n<p><img class=\"alignnone size-full wp-image-211599\" src=\"https://wpforms.com/wp-content/uploads/2018/06/Custom-Email-Header.jpg\" alt=\"Custom Email Header\" width=\"562\" height=\"314\" srcset=\"https://wpforms.com/wp-content/uploads/2018/06/Custom-Email-Header.jpg 562w, https://wpforms.com/wp-content/uploads/2018/06/Custom-Email-Header-300x168.jpg 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<h3>Step 5: Configure Your Subscribe Form’s Confirmations</h3>\n<p>Form confirmations are messages that display to site visitors once they subscribe to your email list. They let people know that their form has been processed, and offer you the chance to let them know what the next steps are.</p>\n<p>WPForms has three confirmation types to choose from:</p>\n<ol>\n<li><strong>Message.</strong> This is the default confirmation type in WPForms. When a site visitor subscribes, a simple message confirmation will appear letting them know their form was processed. Look here for some <a href=\"https://wpforms.com/form-success-message-ideas-to-boost-customer-happiness/\">great success messages</a> to help boost customer happiness.</li>\n<li><strong>Show Page.</strong> This confirmation type will take site visitors to a specific web page on your site thanking them for subscribing. For help doing this, check out our tutorial on <a href=\"https://wpforms.com/how-to-redirect-users-after-wordpress-form-submit/\">redirecting customers to a thank you page</a>. In addition, be sure to check out our article on <a href=\"https://wpforms.com/4-thank-you-page-examples-to-boost-customer-loyalty/\">creating effective Thank You pages</a> for boosting customer loyalty.</li>\n<li><strong>Go to URL (Redirect).</strong> This option is used when you want to send site visitors to a different website.</li>\n</ol>\n<p>Let’s see how to set up a simple form confirmation in WPForms so you can customize the message site visitors will see when they submit a form on your site.</p>\n<p>To start, click on the <strong>Confirmation</strong> tab in the Form Editor under <strong>Settings</strong>.</p>\n<p>Next, select the type of confirmation type you’d like to create. For this example, we’ll select <strong>Message</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-238574\" src=\"https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings.png\" alt=\"WPForms Confirmation Settings\" width=\"562\" height=\"260\" srcset=\"https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings-300x139.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, customize the confirmation message to your liking and click <strong>Save</strong>.</p>\n<p>If you want to display different confirmations to site visitors based on their behavior, be sure to read up on <a href=\"https://wpforms.com/docs/how-to-create-conditional-form-confirmations/\">how to create conditional form confirmations</a>.</p>\n<p>Now you’re ready to add your Drip signup form to your website.</p>\n<h3>Step 6: Add Your Drip Signup Form to Your Website</h3>\n<p>After you’ve created your Drip subscribe form, you need to add it to your WordPress website.</p>\n<p>WPForms allows you to add your forms to many locations on your website, including your blog posts, pages, and even <a href=\"https://wpforms.com/how-to-add-a-contact-form-in-wordpress-sidebar-widget/\">sidebar widgets</a>.</p>\n<p>Let’s take a look at the most common post/page embed option.</p>\n<p>To start, create a new post or page in WordPress and then click on the <strong>Add Form</strong> button.</p>\n<p>Next, select your contact form in the modal popup and click <strong>Add Form</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-207955\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Add-Form.png\" alt=\"Add Form\" width=\"562\" height=\"329\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Add-Form.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Add-Form-300x176.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, publish your post or page so your contact form will appear on your website.</p>\n<p><img class=\"alignnone size-full wp-image-232739\" src=\"https://wpforms.com/wp-content/uploads/2018/07/Final-Subscribe-Form.png\" alt=\"Final Subscribe Form\" width=\"562\" height=\"263\" srcset=\"https://wpforms.com/wp-content/uploads/2018/07/Final-Subscribe-Form.png 562w, https://wpforms.com/wp-content/uploads/2018/07/Final-Subscribe-Form-300x140.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>And there you have it! You now know how to create a Drip signup form in WordPress so you can start building your email list and working on your <a href=\"http://optinmonster.com/beginners-guide-to-email-marketing/\" target=\"_blank\" rel=\"noopener\">email marketing strategies</a>.</p>\n<p>Be sure to check out our helpful article on <a href=\"https://wpforms.com/mobile-lead-generation/\">optimizing your WordPress forms for mobile lead generation</a>, and if you want to see how well your WordPress forms are performing, make sure to check out <a href=\"https://wpforms.com/how-to-track-wordpress-form-conversions-step-by-step/\">how to track your form conversions</a>.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-create-a-drip-signup-form-in-wordpress-updated/\">How to Create a Drip Signup Form in WordPress (Updated)</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://wpforms.com/how-to-create-a-drip-signup-form-in-wordpress-updated/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:57:\"How to Send a PDF Attachment in a Form Notification Email\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:78:\"https://wpforms.com/how-to-send-a-pdf-attachment-in-a-form-notification-email/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://wpforms.com/how-to-send-a-pdf-attachment-in-a-form-notification-email/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 02 Nov 2018 16:47:34 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:19:\"WordPress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:17:\"form notification\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:18:\"email notification\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:15:\"lead generation\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:11:\"lead magnet\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=270034\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:569:\"<p>Want to learn how to send a PDF attachment in a form notification email? Being able to send PDFs to site visitors after they’ve subscribed is one of the easiest ways to deliver the lead magnet you promised in exchange for an email address. In this article, we’ll show you how to send a PDF [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-send-a-pdf-attachment-in-a-form-notification-email/\">How to Send a PDF Attachment in a Form Notification Email</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:11814:\"<p>Want to learn how to send a PDF attachment in a form notification email? Being able to send PDFs to site visitors after they’ve subscribed is one of the easiest ways to deliver the lead magnet you promised in exchange for an email address.</p>\n<p>In this article, we’ll show you how to send a PDF in a form notification email using WPForms.</p>\n<h3>Why Offer Lead Magnets?</h3>\n<p>Lead magnets are one of the best ways to build your email list. Used as an <a href=\"https://wpforms.com/10-research-based-tips-to-improve-contact-form-conversions/\">incentive to get people to subscribe</a>, lead magnets are pieces of high-quality content you can deliver once they successfully sign up.</p>\n<p>Good lead magnets are valuable enough for people to want to sign up for your email list and continue to receive marketing materials from you in the future.</p>\n<p>Some of the most common lead magnets include PDFs in the form of:</p>\n<ul>\n<li>eBooks</li>\n<li>Checklists</li>\n<li>Reports</li>\n<li>Case Studies</li>\n<li>Templates</li>\n<li>Resource Lists</li>\n<li>Guides</li>\n<li>Infographics</li>\n<li>And so much more</li>\n</ul>\n<p><a href=\"http://optinmonster.com/beginners-guide-to-email-marketing/\" target=\"_blank\" rel=\"noopener\">Email marketing is critical</a> if you want to grow your readership or customer base. It’s the best way to communicate with people that are interested in what you have to offer. Plus, you own your email list and have complete control over it at all times.</p>\n<p>Not to mention, email marketing is known for <a href=\"https://www.lyfemarketing.com/blog/email-marketing-roi/\" target=\"_blank\" rel=\"nofollow noopener\">having the highest ROI</a> when compared to digital marketing strategies like SEO and advertisements.</p>\n<p><img class=\"alignnone size-full wp-image-270036\" src=\"https://wpforms.com/wp-content/uploads/2018/09/email-marketing-roi.png\" alt=\"email marketing roi\" width=\"562\" height=\"287\" srcset=\"https://wpforms.com/wp-content/uploads/2018/09/email-marketing-roi.png 562w, https://wpforms.com/wp-content/uploads/2018/09/email-marketing-roi-300x153.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>So, let’s see how to can send your lead magnet to new subscribers in the WPForms email notification.</p>\n<h3>Step 1: Create a lead Magnet</h3>\n<p>The first thing you’ll need to do is create a lead magnet for your subscribe form.</p>\n<p>If you need some inspiration, you can always check out this <a href=\"https://optinmonster.com/9-lead-magnets-to-increase-subscribers/\" target=\"_blank\" rel=\"noopener\">roundup of highly effective lead magnet ideas</a> to help you grow your email list. Though not all of them will be PDFs you can offer, there are plenty of options to choose from.</p>\n<p>Once you’ve created your PDF lead magnet, upload it to your WordPress website.</p>\n<p>To do this, go to <strong>Media</strong> » <strong>Add New</strong> and upload it to your site like you would an image.</p>\n<p><img class=\"alignnone size-full wp-image-173487\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Media-Library.png\" alt=\"Media Library\" width=\"562\" height=\"248\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Media-Library.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Media-Library-300x132.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If the PDF attachment you’re creating is duplicating any content that is already on your website, you might want to hide it from Google so you don’t get penalized in search rankings for having duplicate content. You also don’t want the lead magnet to rank higher than the original piece of content.</p>\n<p>To hide your lead magnet from Google, you can use the robots.txt file of your site to make sure Google doens&#8217;t index it.</p>\n<p>For example, let’s say you’re offering site visitors a PDF version of a blog post and it has a URL that looks like this: http://example.com/2018/12/printable.pdf.</p>\n<p>To hide it from search engines, here is how you’ll add this URL to your robots.txt file.</p>\n<p><code>User-agent: *<br />\nDisallow: /2018/12/printable.pdf</code></p>\n<p>For more help with this step, read up on <a href=\"http://www.wpbeginner.com/wp-tutorials/how-to-hide-a-wordpress-page-from-google/\" target=\"_blank\" rel=\"noopener\">how to hide pages from Google</a>.</p>\n<h3>Step 2: Create a New WordPress Form</h3>\n<p>To create a new WordPress form, you’ll need to install and activate the <a href=\"https://wpforms.com/pricing/\">WPForms plugin</a>. For more details, see this step by step guide on <a title=\"Step by step guide on How to Install a WordPress Plugin\" href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" target=\"_blank\" rel=\"noopener\">how to install a plugin in WordPress</a>.</p>\n<p>Next, you’ll need to create a signup form. For help with this step, see our tutorial on <a href=\" https://wpforms.com/how-to-create-a-mailchimp-subscribe-form-in-wordpress/\">how to create a MailChimp subscribe form</a> as an example to get started.</p>\n<p><img class=\"alignnone size-full wp-image-207966\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview.png\" alt=\"Newsletter Signup Form Preview\" width=\"562\" height=\"348\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Newsletter-Signup-Form-Preview-300x186.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Keep in mind, you can create many types of forms with the WPForms plugin templates:</p>\n<ul>\n<li><a href=\"https://wpforms.com/how-to-create-a-file-upload-form-in-wordpress/\">File upload forms</a> to gather additional information from site visitors</li>\n<li><a href=\"https://wpforms.com/how-to-create-a-job-application-form-in-wordpress/\">Job application forms</a> to fill an open position that’s available</li>\n<li><a href=\"https://wpforms.com/create-a-survey-form-in-wordpress-step-by-step/\">Survey forms</a> to gather customer feedback about your business</li>\n<li><a href=\"https://wpforms.com/how-to-create-a-user-registration-form-in-wordpress/\">User registration forms</a> so people can sign up for subscriptions, services, or other plans</li>\n<li><a href=\"https://wpforms.com/how-to-create-a-nonprofit-donation-form/\">Donation forms</a> so you can collect money for your cause or organization</li>\n<li>And any number of subscribe forms using our <a href=\"http://www.wpbeginner.com/showcase/best-email-marketing-services/\" target=\"_blank\" rel=\"noopener\">email service provider</a> integrations: <a href=\"https://wpforms.com/addons/mailchimp-addon/\">MailChimp</a>, <a href=\"https://wpforms.com/addons/aweber-addon/\">AWeber</a>, <a href=\"https://wpforms.com/features/constant-contact/\">Constant Contact</a>, <a href=\"https://wpforms.com/addons/campaign-monitor-addon/\">Campaign Monitor</a>, <a href=\"https://wpforms.com/addons/drip-addon/\">Drip</a>, and <a href=\"https://wpforms.com/addons/getresponse-addon/\">GetResponse</a></li>\n</ul>\n<p>All of these forms, and the hundred more you can create with the WPForms <a href=\"https://wpforms.com/features/form-templates/\">Form Templates addon</a>, can have a <a href=\"https://wpforms.com/how-to-add-an-aweber-signup-checkbox-to-your-contact-form/\">signup checkbox added to them</a> to encourage people to subscribe to your email list in exchange for a lead magnet.</p>\n<p>Just make sure that the form you create includes details about the lead magnet people will receive if they sign up.</p>\n<p><img class=\"alignnone size-full wp-image-173490\" src=\"https://wpforms.com/wp-content/uploads/2018/03/Content-Upgrade-Newsletter-Form.png\" alt=\"Content Upgrade Newsletter Form\" width=\"562\" height=\"376\" srcset=\"https://wpforms.com/wp-content/uploads/2018/03/Content-Upgrade-Newsletter-Form.png 562w, https://wpforms.com/wp-content/uploads/2018/03/Content-Upgrade-Newsletter-Form-300x201.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<h3>Step 3: Add the PDF to Your Form’s Notification Email</h3>\n<p>To add your PDF lead magnet to your form’s notification email, first go to <strong>Media</strong> » <strong>Library</strong> and find the PDF attachment you uploaded to your website that will serve as your lead magnet.</p>\n<p>Then, copy the lead magnet’s URL.</p>\n<p><img class=\"alignnone size-full wp-image-270038\" src=\"https://wpforms.com/wp-content/uploads/2018/09/pdf-in-media-library.png\" alt=\"pdf in media library\" width=\"562\" height=\"414\" srcset=\"https://wpforms.com/wp-content/uploads/2018/09/pdf-in-media-library.png 562w, https://wpforms.com/wp-content/uploads/2018/09/pdf-in-media-library-300x221.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, go to <strong>Settings</strong> » <strong>Notifications</strong> in the form editor.</p>\n<p><img class=\"alignnone size-full wp-image-270037\" src=\"https://wpforms.com/wp-content/uploads/2018/09/form-notification-settings.png\" alt=\"form notification settings\" width=\"562\" height=\"344\" srcset=\"https://wpforms.com/wp-content/uploads/2018/09/form-notification-settings.png 562w, https://wpforms.com/wp-content/uploads/2018/09/form-notification-settings-300x184.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, in the <strong>Message</strong> section, paste this HTML, complete with the URL of your PDF and the text you want for the link:</p>\n<p>&lt;a href=&quot;COPIEDURL&quot; target=&quot;_blank&quot; &gt;Text for link&lt;/a&gt;</p>\n<p>So, for our example, our notification’s message section will look like this:</p>\n<p><img class=\"alignnone size-full wp-image-270035\" src=\"https://wpforms.com/wp-content/uploads/2018/09/add-pdf-attachment-to-notification-email.png\" alt=\"add pdf attachment to notification email\" width=\"562\" height=\"369\" srcset=\"https://wpforms.com/wp-content/uploads/2018/09/add-pdf-attachment-to-notification-email.png 562w, https://wpforms.com/wp-content/uploads/2018/09/add-pdf-attachment-to-notification-email-300x197.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Click <strong>Save</strong>.</p>\n<p>Now, any time someone fills out your signup form and subscribes, they will receive a notification email from you that includes the lead magnet you promised them for subscribing.</p>\n<p>And there you have it! You now know how to send a PDF attachment in a form notification email to people when they sign up for your email list.</p>\n<p>If you find that your email notifications aren’t delivering the way they’re supposed to, be sure to read up on <a href=\"https://wpforms.com/how-to-fix-wordpress-contact-form-not-sending-email-issue/\">how to get WordPress from notifications using the WP Mail SMTP plugin</a> so that you know every site visitor that subscribes to your email list gets their lead magnet.</p>\n<p>And if you want to learn how to create a lead magnet and send it in a marketing email or add it to your form’s confirmation messages, check out this helpful tutorial on <a href=\"https://wpforms.com/how-to-create-a-simple-lead-magnet-optin-form-in-wordpress/\">how to create a simple lead magnet optin form in WordPress</a>.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-send-a-pdf-attachment-in-a-form-notification-email/\">How to Send a PDF Attachment in a Form Notification Email</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:83:\"https://wpforms.com/how-to-send-a-pdf-attachment-in-a-form-notification-email/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:5;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:56:\"How to Accept Recurring Payments on Your WordPress Forms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:77:\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 01 Nov 2018 12:00:36 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:19:\"WordPress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:6:\"stripe\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:37:\"non-profit donation form in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:30:\"conditional payment processing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:16:\"stripe recurring\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=305183\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:625:\"<p>Are you wondering how to accept recurring payments on your WordPress forms? Generating revenue is often a website owner’s number one goal, and achieving that goal is whole lot easier when you can accept continual payments from repeat customers. In this article, we’ll show you how WPForms makes accepting recurring payments on your WordPress forms [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/\">How to Accept Recurring Payments on Your WordPress Forms</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:11197:\"<p>Are you wondering how to accept recurring payments on your WordPress forms? Generating revenue is often a website owner’s number one goal, and achieving that goal is whole lot easier when you can accept continual payments from repeat customers.</p>\n<p>In this article, we’ll show you how WPForms makes accepting recurring payments on your WordPress forms simple with Stripe so you can focus on more important aspects of building your business.</p>\n<h3>Why Accepts Recurring Payments on Your WordPress Forms?</h3>\n<p>You might be asking yourself why recurring payment functionality is important to have on your WordPress forms. Well, when it comes to generating revenue, forming solid relationships with long-term customers is the key to your success.</p>\n<p>And, it just so happens that being able to accept recurring payments on your WordPress forms helps you achieve that success because:</p>\n<ul>\n<li>It costs five times more to acquire a new customer than it does to maintain an existing customer. By giving existing customers the option to pay you on a regular basis for products or services, you save money and <strong><a href=\"http://www.wpbeginner.com/beginners-guide/make-money-online/\" target=\"_blank\" rel=\"noopener\">make money online</a></strong> with ease.</li>\n<li>Repeat business through recurring payments help forge better, longer-lasting relationships with your target audience.</li>\n<li>You can collect steady income when you accept recurring payments for things like monthly products, subscriptions, memberships, or other services.</li>\n<li>Enabling recurring payment functionality gives your business room to scale.</li>\n<li>Accepting recurring payments takes less effort on your part, and makes things easier on your customers too.</li>\n</ul>\n<p>Whether you have an <a href=\"https://wpforms.com/how-to-create-a-donation-form-popup-in-wordpress/\">online donation form</a>, a <a href=\"https://wpforms.com/how-to-create-a-user-registration-form-in-wordpress/\">user registration form</a> to access locked content, or a <a href=\"https://wpforms.com/how-to-create-a-simple-order-form-in-wordpress/\">simple order form</a> that sells physical or even <a href=\"https://wpforms.com/the-simplest-way-to-sell-digital-products-on-your-wordpress-site/\">digital products</a>, WPForms has you covered if you want to accept recurring payments.</p>\n<p>So, let’s take a look at how to enable recurring payment functionality on your WordPress forms using WPForms and Stripe.</p>\n<h3>Step 1: Create a New WordPress Form</h3>\n<p>The first thing you’ll need to do is install and activate the <a href=\"https://wpforms.com/pricing/\">WPForms plugin</a>. For more details, see this step by step guide on <a title=\"Step by step guide on How to Install a WordPress Plugin\" href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" target=\"_blank\" rel=\"noopener\">how to install a plugin in WordPress</a>.</p>\n<p>WPForms allows you to accept recurring payments on your forms using <a href=\"https://wpforms.com/addons/stripe-addon/\">Stripe</a>. That’s why the next step is to install and activate the Stripe addon.</p>\n<p>To do this, go to <strong>WPForms</strong> » <strong>Addon</strong> and find the one labeled <strong>Stripe addon</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-305192\" src=\"https://wpforms.com/wp-content/uploads/2018/11/stripe-addon.png\" alt=\"stripe addon\" width=\"562\" height=\"169\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/stripe-addon.png 562w, https://wpforms.com/wp-content/uploads/2018/11/stripe-addon-300x90.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, go to <strong>WPForms » Add New</strong> to create a new form.</p>\n<p>For our example, we’ll create an online donation form that gives people the option to make a one-time donation or donate on a recurring basis. For help with this step, check out our tutorial on <a href=\"https://wpforms.com/how-to-create-a-nonprofit-donation-form/\">creating a nonprofit donation form in WordPress</a>.</p>\n<p>This is what our donation form looks like before customizing any of the form fields.</p>\n<p><img class=\"alignnone size-full wp-image-305190\" src=\"https://wpforms.com/wp-content/uploads/2018/11/donation-form-without-customization.png\" alt=\"donation form without customization\" width=\"562\" height=\"486\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/donation-form-without-customization.png 562w, https://wpforms.com/wp-content/uploads/2018/11/donation-form-without-customization-300x260.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<h3>Step 2: Customize Your WordPress Form Fields</h3>\n<p>You can add additional fields to your form by dragging them from the left hand panel to the right hand panel.</p>\n<p>Then, click on the field to make any changes. You can also click on a form field and drag it to rearrange the order on your donation form.</p>\n<p>In order to accept recurring payments using Stripe, you’ll have to add at the very least these two form fields for your form to work right:</p>\n<ol>\n<li>An items field such as Single Item, Multiple Items, or Dropdown Items</li>\n<li>A Credit Card field</li>\n</ol>\n<p>You can also add a <strong>Total</strong> form field so customers know exactly how much they’re being charged when they submit your form.</p>\n<p><img class=\"alignnone size-full wp-image-310978\" src=\"https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-form-fields-on-donation-form.png\" alt=\"recurring payment form fields on donation form\" width=\"562\" height=\"458\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-form-fields-on-donation-form.png 562w, https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-form-fields-on-donation-form-300x244.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>As you can see, we added a dropdown menu so people can choose whether they want to donate on a recurring basis or make a one-time payment, as well as a field for selecting how much people want to donate, a Total field, and the Credit Card field.</p>\n<p>When your form looks the way you want it to, click <strong>Save</strong>.</p>\n<h3>Step 3: Configure Your Payment Settings</h3>\n<p>When your form is ready, go to <strong>Payments</strong> » <strong>Stripe</strong> to configure the payment settings so your form can accept payments from people using the Stripe payment gateway.</p>\n<p>Next, click the checkbox labeled <strong>Enable Stripe payments</strong>, give your payment a description, and select <strong>Email</strong> as the <strong>Stripe Payment Receipt</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-305193\" src=\"https://wpforms.com/wp-content/uploads/2018/11/stripe-settings.png\" alt=\"stripe settings\" width=\"562\" height=\"223\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/stripe-settings.png 562w, https://wpforms.com/wp-content/uploads/2018/11/stripe-settings-300x119.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you were only accepting one-time donation from people, you would be all set. The next step would be to <a href=\"https://wpforms.com/how-to-add-a-contact-form-in-your-wordpress-post-content/\">add your donation form to your website</a>.</p>\n<p>However, you want to set up recurring donations so you have to continue customizing.</p>\n<p>To do this, scroll down a bit more on the Stripe settings panel and click the checkbox labeled <strong>Enable recurring subscription payments</strong> under the <strong>Subscriptions</strong> section.</p>\n<p><img class=\"alignnone size-full wp-image-305191\" src=\"https://wpforms.com/wp-content/uploads/2018/11/enable-recurring-payments.png\" alt=\"enable recurring payments\" width=\"562\" height=\"431\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/enable-recurring-payments.png 562w, https://wpforms.com/wp-content/uploads/2018/11/enable-recurring-payments-300x230.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You will also have to configure the following:</p>\n<ul>\n<li><strong>Plan Name:</strong> this is the title of the subscription and will appear on the Stripe transaction.</li>\n<li><strong>Recurring Period:</strong> choose how often people will be able to make recurring payments. For instance, weekly, monthly, yearly, etc.</li>\n<li><strong>Customer Email:</strong> by selecting <strong>Email</strong> from the dropdown menu, WPForms will be able to send a notification to the customer once they submit their form. Being able to contact people via the email they input on the form is required for Stripe recurring functionality to work.</li>\n</ul>\n<p>Lastly, you’ll need to set the conditional logic rules since you give people the option to make recurring donations or a one-time donation.</p>\n<p>To do this, first select the checkbox <strong>Enable conditional logic</strong>.</p>\n<p>Then, to make things super simple, configure the rule to say: <strong>Process</strong> payment as recurring if <strong>Dropdown</strong> is <strong>Monthly</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-310982\" src=\"https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-conditional-logic-rule.png\" alt=\"recurring payment conditional logic rule\" width=\"562\" height=\"134\" srcset=\"https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-conditional-logic-rule.png 562w, https://wpforms.com/wp-content/uploads/2018/11/recurring-payment-conditional-logic-rule-300x72.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>This way, any time someone wants to make recurring donations to your organization, their payment will process automatically according to the time interval you offered on your form.</p>\n<p>Click <strong>Save</strong>.</p>\n<p>Before adding your donation form to your website, make sure to test it to make sure everything processes correctly. For help with this, we have documentation walking you through <a href=\"https://wpforms.com/docs/how-to-test-stripe-payments-on-your-site/\">how to test Stripe payments before accepting actual payments</a>.</p>\n<p>And there you have it! You now know how to accept recurring payments on your WordPress forms using the WPForms Stripe addon.</p>\n<p>If you&#8217;re ready to start accepting recurring payments, make sure to check out our article on <a href=\"https://wpforms.com/how-to-combat-order-form-abandonment-and-increase-your-sales/\">how to combat order form abandonment</a> so you can grow your sales, and donations, and maintain your success.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/\">How to Accept Recurring Payments on Your WordPress Forms</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:82:\"https://wpforms.com/how-to-accept-recurring-payments-on-your-wordpress-forms/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:6;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"9 Top Online Fundraising Ideas for Nonprofit Organizations\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:60:\"https://wpforms.com/online-fundraising-ideas-for-nonprofits/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"https://wpforms.com/online-fundraising-ideas-for-nonprofits/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 31 Oct 2018 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:6:{i:0;a:5:{s:4:\"data\";s:9:\"Marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"donation\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:34:\"donation form plugin for wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:37:\"non-profit donation form in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:25:\"nonprofit wordpress forms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:15:\"email marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=157684\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:590:\"<p>Are you looking for some online fundraising ideas to raise money for your nonprofit organization? Getting involved in online nonprofit fundraising not only taps into a whole new audience base, but offers your organization a fresh new approach to raising money for your cause. In this article, we’ll share with you some of the top [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/online-fundraising-ideas-for-nonprofits/\">9 Top Online Fundraising Ideas for Nonprofit Organizations</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:23321:\"<p>Are you looking for some online fundraising ideas to raise money for your nonprofit organization? Getting involved in online nonprofit fundraising not only taps into a whole new audience base, but offers your organization a fresh new approach to raising money for your cause.</p>\n<p>In this article, we’ll share with you some of the top online fundraising ideas so you can gather some inspiration for your next fundraising event.</p>\n<h3>1. Online Donation Forms</h3>\n<p>One of the most traditional ways of raising money online for a nonprofit is to use a donation form.</p>\n<p>Take for example <a href=\"https://donate.mozilla.org/en-US/\" target=\"_blank\" rel=\"nofollow noopener\">Mozilla</a>. They rely on donations, made through their donation form, to support their cause of keeping the web open and free.</p>\n<p><img class=\"alignnone size-full wp-image-304427\" src=\"https://wpforms.com/wp-content/uploads/2018/10/online-donation-form-example.png\" alt=\"online donation form example\" width=\"562\" height=\"354\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/online-donation-form-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/online-donation-form-example-300x189.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You can collect donations online using the <a href=\"https://wpforms.com\">WPForms plugin</a> and any of the various payment addons, such as <a href=\"https://wpforms.com/addons/paypal-standard-addon/\">PayPal</a> or <a href=\"https://wpforms.com/addons/stripe-addon/\">Stripe</a>.</p>\n<p>Plus, you&#8217;ll know your online donation form <a href=\"https://wpforms.com/mobile-lead-generation/\">is mobile friendly</a> so all site visitors can easily access your donation form and donate.</p>\n<p>Check out our easy to follow tutorial on how to <a href=\"https://wpforms.com/how-to-create-a-non-profit-donation-form/\">create a nonprofit donation form in WordPress</a> and start collecting money for your organization right now.</p>\n<p>And if you want to accept recurring donations, like Mozilla does in the example above, you can! Our Stripe addon now supports recurring payments so anyone wanting to donate to your organization on a regular basis can without having to fill out your online donation form several times.</p>\n<p>For help with setting up Stripe recurring functionality, check out our documentation on <a href=\"https://wpforms.com/docs/how-to-install-and-use-the-stripe-addon-with-wpforms/\">how to use the Stripe addon for WPForms</a>.</p>\n<p>In addition, look here for some <a href=\"https://wpforms.com/donation-page-examples-to-inspire-your-online-fundraising/\">inspiring donation page examples</a> and helpful tips so you can create a donation page that will encourage supporters to take action.</p>\n<p>Want to make sure your donation forms are converting and people are donating to your cause?</p>\n<p>The easiest way to track WordPress form conversions is by using <a href=\"https://www.monsterinsights.com/\" target=\"_blank\" rel=\"noopener\">MonsterInsights</a>.</p>\n<p>MonsterInsights is the best Google Analytics plugin on the market today. It gives you the ability to easily set up Google Analytics on your website and track form views, submissions, and conversions using the <a href=\"https://www.monsterinsights.com/addon/forms/\" target=\"_blank\" rel=\"noopener\">Forms addon</a>.</p>\n<h3>2. Crowdfunding</h3>\n<p>Donation-based crowdfunding is one of the most popular ways nonprofit organizations raise money. This is because crowdfunding takes advantage of already established networks of family, friends, supporters, coworkers, and peers to bring in large donations.</p>\n<p>Crowdfunding is effective for a number of reasons:</p>\n<ul>\n<li>Crowdfunding platforms have the potential to reach a large audience, including those outside your established network.</li>\n<li>You have the opportunity to engage with supporters on the platform itself, as well as interact with people who visit your website and social media accounts from your crowdfunding page</li>\n<li>Your campaign is easy to market across many channels such as your website, in email campaigns, and even on social media</li>\n<li>It&#8217;s efficient for streamlining your online fundraising efforts into one convenient location where you can share updates, collect donations, and interact with supporters</li>\n</ul>\n<p>If you still aren’t sure what crowdfunding is all about, take a look at the well-known <a href=\"https://www.gofundme.com/\" target=\"_blank\" rel=\"nofollow noopener\">GoFundMe campaigns</a>.</p>\n<p><img class=\"alignnone size-full wp-image-304423\" src=\"https://wpforms.com/wp-content/uploads/2018/10/crowdfunding-example.png\" alt=\"crowdfunding example\" width=\"562\" height=\"449\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/crowdfunding-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/crowdfunding-example-300x240.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>GoFundMe is a perfect example of a donation-based crowdfunding platform that nonprofits use to raise money.</p>\n<p>You can easily add a description of your cause, posts status updates and allow comments, collect donations, and add imagery to complement your story. You can even include a link back to your organization&#8217;s website so people can learn more about what you do.</p>\n<h3>3. Peer-to-Peer Fundraising Websites</h3>\n<p>This method of online fundraising uses the support of people that donate on your behalf. For instance, you might run a campaign that lasts a fixed amount of time and has an overall fundraising goal. Each participant then sets their own mini goal and attempts to raise enough money to meet or surpass their goal.</p>\n<p>Another type includes personal challenges, such as <a href=\"https://no-shave.org/\" target=\"_blank\" rel=\"nofollow noopener\">No Shave November</a>, which involves people not shaving for a month to raise money for cancer research.</p>\n<p>Lastly, there are the activity-based peer-to-peer fundraising campaigns that center around activities such as walking, cycling, swimming, or running. One great example of this is the <a href=\"https://ww5.komen.org/RaceForTheCure/\" target=\"_blank\" rel=\"nofollow noopener\">Susan G. Komen Race for the Cure</a>.</p>\n<p><img class=\"alignnone size-full wp-image-304428\" src=\"https://wpforms.com/wp-content/uploads/2018/10/peer-to-peer-fundraising-idea.png\" alt=\"peer to peer fundraising idea\" width=\"562\" height=\"517\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/peer-to-peer-fundraising-idea.png 562w, https://wpforms.com/wp-content/uploads/2018/10/peer-to-peer-fundraising-idea-300x276.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Activity-based peer-to-peer fundraising is highly popular, tends to have a lot of active participation, and <a href=\"http://hi.blackbaud.com/p2p/\" target=\"_blank\" rel=\"nofollow noopener\">brings in a lot of online donations</a>.</p>\n<p><img class=\"alignnone size-full wp-image-304421\" src=\"https://wpforms.com/wp-content/uploads/2018/10/online-donation-stats.png\" alt=\"online donation stats\" width=\"562\" height=\"354\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/online-donation-stats.png 562w, https://wpforms.com/wp-content/uploads/2018/10/online-donation-stats-300x189.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>It is also thought that <a href=\"https://www.classy.org/blog/infographic-the-value-of-peer-to-peer-fundraising/\" target=\"_blank\" rel=\"nofollow noopener\">1 in 4 email campaigns for peer-to-peer fundraisers</a> result in donations, as opposed to 1 in 1,250 emails sent by nonprofit organizations.</p>\n<p>Activity-based fundraisers usually work like this:</p>\n<ul>\n<li>You, the nonprofit organization, set a timeline and plan a peer-to-peer event</li>\n<li>You then reach out to loyal supporters and ask them if they want to participate in the peer-to-peer event</li>\n<li>If supporters want to participate, they create their own way of fundraising by either setting up their own donation page or utilizing your resources, such as your organization’s website</li>\n<li>Participants then reach out to those they know asking for donations on behalf of your organization</li>\n<li>Supporters of the participants donate through the peer-to-peer fundraising webpage</li>\n</ul>\n<p>You then collect donations, acknowledge participants and donors, and host the event.</p>\n<p>The great thing about WPForms, and the <a href=\"https://wpforms.com/features/form-templates/\">Form Templates Pack addon</a>, is that there are plenty of form templates that can help you with your activity-based fundraiser:</p>\n<ul>\n<li>The basic Fundraiser Registration form for any type of fundraiser you want to host</li>\n<li>A Race Registration form, which can easily be customized to accommodate any fundraising activity</li>\n<li>The Online Event Registration form that can accompany your activity-based fundraiser and generate more funds for your organization</li>\n</ul>\n<p>In addition, you can always <a href=\"https://wpforms.com/how-to-set-up-a-volunteer-application-form-in-wordpress/\">create a volunteer application form</a> to staff your upcoming charity events so those participating in your activity-based fundraiser have a great experience.</p>\n<p>In addition, they’ll have nothing but stellar things to say when you <a href=\"https://wpforms.com/how-to-create-a-popup-feedback-form-in-wordpress/\">add an online survey</a> to your site to see how people felt about your event and whether there are improvements needed for next time.</p>\n<p>Lastly, if you need help organizing your event and volunteers, <a href=\"https://wpforms.com/how-to-send-wpforms-entries-to-google-calendar/\">connect your WordPress forms to your Google Calendar</a> using our <a href=\"https://wpforms.com/addons/zapier-addon/\">Zapier addon</a> and have all the information you need calendared automatically so you never miss a beat.</p>\n<h3>4. Fundraising Emails</h3>\n<p>One unique way to take advantage of your growing email list is to send out email campaigns asking for donations.</p>\n<p>If you want to take it even further and <a href=\"https://wpforms.com/email-personalization-examples/\">personalize your emails</a>, you might even consider segmenting your mailing lists so that only subscribers that show an interest in your cause are targeted in your <a href=\"http://optinmonster.com/beginners-guide-to-email-marketing/\" target=\"_blank\" rel=\"noopener\">email marketing efforts</a>.</p>\n<p>If you don’t like the idea of sending out regular email campaigns asking for money, consider doing a one-day email blast.</p>\n<p>Choose a day that holds some importance to your organization, and as a way to commemorate that day, send out an email asking subscribers for donations. Make sure to include a personal story, add some visual elements, and include a clear call to action that expresses urgency.</p>\n<p><a href=\"http://www.feedingamerica.org/\" target=\"_blank\" rel=\"nofollow noopener\">Feeding America</a> does a good job of adding all of these elements to their email campaigns.</p>\n<p><img class=\"alignnone size-full wp-image-304420\" src=\"https://wpforms.com/wp-content/uploads/2018/10/email-campaign-fundraising.png\" alt=\"email campaign fundraising\" width=\"562\" height=\"635\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/email-campaign-fundraising.png 562w, https://wpforms.com/wp-content/uploads/2018/10/email-campaign-fundraising-266x300.png 266w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Most importantly, they have clear CTA buttons that take you to different parts of their website. The one labeled <strong>Take Action</strong> links right to the site’s donation page, where you can easily make a donation.</p>\n<p><img class=\"alignnone size-full wp-image-304424\" src=\"https://wpforms.com/wp-content/uploads/2018/10/cta-on-email-campaign.png\" alt=\"cta on email campaign\" width=\"562\" height=\"404\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/cta-on-email-campaign.png 562w, https://wpforms.com/wp-content/uploads/2018/10/cta-on-email-campaign-300x216.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you have strong ties with a major corporation or donor that supports your cause, you might want to ask if they’ll match any donations you receive from your email blast. This way those that donate feel their money is getting put to even better use because it&#8217;s doubling in size.</p>\n<p>For help with getting more sponsors supporting your cause, make sure to create sponsorship request form with WPForms and add it to your website so those that are interested can sign up with ease.</p>\n<p>In the end, the reason email fundraising is so effective is twofold. First, you can reach a large number of people at one time, <a href=\"http://www.wpbeginner.com/beginners-guide/how-to-build-an-email-list-in-wordpress-email-marketing-101/\" target=\"_blank\" rel=\"nofollow noopener\">assuming you have a big email list</a>. Second, it makes donating on behalf of your organization very simple for supporters.</p>\n<h3>5. T-Shirt Fundraising</h3>\n<p>A fun way of raising money for your cause is to sell t-shirts. After all, t-shirts are a popular item that anyone can use. That&#8217;s why it&#8217;s one of the top fundraising ideas for nonprofit organizations.</p>\n<p>Just look at <a href=\"http://gividaactivewear.com/\" target=\"_blank\" rel=\"nofollow noopener\">Givida Activewear</a>. They sell women’s clothing in support of ending world hunger.</p>\n<p><img class=\"alignnone size-full wp-image-304430\" src=\"https://wpforms.com/wp-content/uploads/2018/10/t-shirt-fundraising-example.png\" alt=\"t-shirt fundraising example\" width=\"562\" height=\"534\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/t-shirt-fundraising-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/t-shirt-fundraising-example-300x285.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Doubling as marketing materials for your nonprofit’s brand, t-shirts are an inexpensive way for people to donate, even on your very own website. And, it just so happens that WPForms has a way for you to <a href=\"https://wpforms.com/how-to-create-a-t-shirt-order-form-in-wordpress-step-by-step/\">create a t-shirt order form right in WordPress</a>.</p>\n<p>Just make sure you follow some of these helpful tips when creating your online form:</p>\n<ul>\n<li>Provide plenty of customization options, such as color and size</li>\n<li>Make sure to <a href=\"https://wpforms.com/add-image-to-wordpress-form\">add an image to your form</a> so people know what they are ordering</li>\n<li>Consider allowing people to <a href=\"https://wpforms.com/docs/how-to-create-a-donation-form-with-multiple-amounts/\">set their own donation amount</a>, in addition to the set amounts</li>\n</ul>\n<p>Just make sure to give people a sense that they are donating to a good cause, and not just padding your bottom line.</p>\n<p>People are more likely to donate when they feel you are sincere. Plus, feeling like they are a part of something good will make people more excited to wear their newly purchased t-shirt and spread the word about your cause.</p>\n<h3>6. Facebook Fundraising</h3>\n<p>Not using your social media networks as a way to raise funds for your nonprofit is a mistake given the power of social media.</p>\n<p>And, if you are using WPForms on your website for your site’s donation form, you can easily connect your Facebook platform to WPForms using the <a href=\"https://wpforms.com/addons/zapier-addon/\">Zapier addon</a> to easily share posts from your site directly to Facebook.</p>\n<p>The great thing about Facebook is that it’s popular, which means your organization will be widely seen. And, by adding a simple “Donate” button, like the <a href=\"https://www.facebook.com/redcross/\" target=\"_blank\" rel=\"nofollow noopener\">America Red Cross</a> has done, you can easily collect the funds needed to keep your organization running.</p>\n<p><img class=\"alignnone size-full wp-image-304425\" src=\"https://wpforms.com/wp-content/uploads/2018/10/donate-button-example-in-social-media.png\" alt=\"donate button example in social media\" width=\"562\" height=\"200\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/donate-button-example-in-social-media.png 562w, https://wpforms.com/wp-content/uploads/2018/10/donate-button-example-in-social-media-300x107.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Plus, you can use your social media reach to direct people back to your nonprofit website to get updates on what is happening with your organization, subscribe to your newsletter, and even make donations through your donation form. The possibilities are endless.</p>\n<h3>7. Donation Matching</h3>\n<p>If you have a Rolodex of willing sponsors, leverage their wealth and run a donation matching drive to double your earnings.</p>\n<p>People love to know the money they’re donating is being put to good use. Even more so, people love to donate knowing that someone supporting the same cause as them will match the amount they donate.</p>\n<p>In fact, <a href=\"http://casefoundation.org/wp-content/uploads/2014/11/MillennialImpactReport-2013.pdf\" target=\"_blank\" rel=\"nofollow noopener\">millennials are known for wanting to donate</a> to a charity because they know their gift will be matched. No matter how small the donation, they want to know their money is helping the issues they support and is achieving tangible results.</p>\n<p>Take for instance, <a href=\"https://www.grey2kusa.org/givingtuesday/\" target=\"_blank\" rel=\"nofollow noopener\">Grey2K</a>, a nonprofit trying to put an end to greyhound racing:</p>\n<p><img class=\"alignnone size-full wp-image-304426\" src=\"https://wpforms.com/wp-content/uploads/2018/10/donation-matching-example.png\" alt=\"donation matching example\" width=\"562\" height=\"685\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/donation-matching-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/donation-matching-example-246x300.png 246w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Using two of their supporters, Grey2K has agreed to match any amount given, which doubles their collected funds and further helps them support their cause.</p>\n<h3>8. Pinterest Boards</h3>\n<p><a href=\"http://www.nptechforgood.com/2015/09/16/20-must-know-fundraising-and-social-media-stats/\" target=\"_blank\" rel=\"nofollow noopener\">64% of all donations are made by women</a>. And, since <a href=\"https://www.businessinsider.com/pinterest-as-a-brand-platform-2014-11\" target=\"_blank\" rel=\"nofollow noopener\">81% of Pinterest users are women</a>, it makes sense that targeting that social media platform to raise money makes sense.</p>\n<p>Use infographics, images, and quotes to grab people’s attention as they scroll through Pinterest looking for ideas. But don’t forget to add a button on your pin that links right back to your website’s online donation form so people can donate right then and there.</p>\n<p><a href=\"https://www.nature.org/en-us/about-us/where-we-work/united-states/virginia/\" target=\"_blank\" rel=\"nofollow noopener\">The Nature Conservancy</a> does a good job of grabbing people’s attention with a large quote on their Pinterest board. They also have a button linking right to their website, which leads people to information about their cause and links to their donation page.</p>\n<p><img class=\"alignnone size-full wp-image-304429\" src=\"https://wpforms.com/wp-content/uploads/2018/10/pinterest-button-example.png\" alt=\"pinterest button example\" width=\"562\" height=\"689\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/pinterest-button-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/pinterest-button-example-245x300.png 245w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>This strategy is just another way to reach a wider audience and raise more funds for a worthy cause, and it can work for you too.</p>\n<h3>9. Run a Contest</h3>\n<p>Not only does WPForms have a contest form template that makes setting up and running contests a cinch, it also has various form permissions and access controls so you can make sure only those you want submitting your forms can.</p>\n<p>With the <a href=\"https://wpforms.com/addons/form-locker-addon/\">Form Locker addon</a>, you can customize your contest forms in the following ways:</p>\n<ul>\n<li><a href=\"https://wpforms.com/how-to-password-protect-wordpress-forms-step-by-step/\">Password protect forms</a> so only those with the password can submit an entry to your contest</li>\n<li><a href=\"https://wpforms.com/how-to-enable-wordpress-form-scheduling-step-by-step/\">Close your contest form</a> on a specified date and time to control the number of entries received</li>\n<li><a href=\"https://wpforms.com/how-to-limit-the-number-of-wordpress-form-entries/\">Limit the number of form submissions</a></li>\n<li><a href=\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/\">Restrict form submissions</a> to those that are logged into your website, encouraging non-logged in users to register using your <a href=\"https://wpforms.com/how-to-create-a-user-registration-form-in-wordpress/\">user registration form</a></li>\n</ul>\n<p>Of course, you may not want to limit your contest form in any way so you can raise as much money as possible. However, to keep things under control, it’s nice to know these features are available just in case.</p>\n<p>If you want to give away a large prize, in exchange for generous donations, talk to local businesses and see if they’ll donate something of value, just like <a href=\"https://www.browncountyunitedway.org/\" target=\"_blank\" rel=\"nofollow noopener\">Brown County United Way</a> has done in the past.</p>\n<p><img class=\"alignnone size-full wp-image-304422\" src=\"https://wpforms.com/wp-content/uploads/2018/10/contest-giveaway-example.png\" alt=\"contest giveaway example\" width=\"562\" height=\"647\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/contest-giveaway-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/contest-giveaway-example-261x300.png 261w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, in order for people to be eligible to win, they must donate a minimum amount to enter.</p>\n<p>And there you have it! We hope that this roundup of online fundraising ideas for nonprofit organizations gives you the inspiration needed to start raising money for your cause so you can meet your online fundraising goals.</p>\n<p>If you liked this article, you might want to read about <a href=\"https://wpforms.com/6-tactics-to-boost-donation-form-conversions/\">how to boost donation form conversions</a> before you start your online fundraising campaign.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/online-fundraising-ideas-for-nonprofits/\">9 Top Online Fundraising Ideas for Nonprofit Organizations</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:65:\"https://wpforms.com/online-fundraising-ideas-for-nonprofits/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:7;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"How to Automatically Send a WordPress Order Form Email Receipt\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:83:\"https://wpforms.com/how-to-automatically-send-a-wordpress-order-form-email-receipt/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:92:\"https://wpforms.com/how-to-automatically-send-a-wordpress-order-form-email-receipt/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 30 Oct 2018 12:00:57 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:19:\"WordPress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:17:\"form notification\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:10:\"order form\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:18:\"email notification\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:13:\"email receipt\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=134564\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:633:\"<p>Do you need to automatically send a WordPress order form email receipt to your customers from your WordPress website? By doing this, not only do you save yourself time from having to send out separate email receipts, you strengthen the relationship you have building with your customers. After all, anytime you automatically provide information to [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-automatically-send-a-wordpress-order-form-email-receipt/\">How to Automatically Send a WordPress Order Form Email Receipt</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:15946:\"<p>Do you need to automatically send a WordPress order form email receipt to your customers from your WordPress website? By doing this, not only do you save yourself time from having to send out separate email receipts, you strengthen the relationship you have building with your customers.</p>\n<p>After all, anytime you automatically provide information to a user regarding a form submission helps establish your reputation as being reliable and trustworthy.</p>\n<p>In this article, we will show you how to automatically send a WordPress order form email receipt.</p>\n<h3>Step 1: Create an Order Form in WordPress</h3>\n<p>The first thing you need to do is install and activate the <a href=\"https://wpforms.com/pricing/\">WPForms plugin</a>. If you need more details, check out this step by step guide on <a title=\"How to Install a WordPress Plugin – Step by Step for Beginners\" href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" target=\"_blank\" rel=\"noopener\">how to install a WordPress plugin</a>.</p>\n<p>Next, go to <strong>WPForms</strong> » <strong>Add New</strong> to create a new order form.</p>\n<p><img class=\"alignnone size-full wp-image-302722\" src=\"https://wpforms.com/wp-content/uploads/2018/10/add-new-wordpress-form.png\" alt=\"add new wordpress form\" width=\"562\" height=\"269\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/add-new-wordpress-form.png 562w, https://wpforms.com/wp-content/uploads/2018/10/add-new-wordpress-form-300x144.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>On the setup screen, name your form and select the <strong>Billing/Order Form </strong>template.</p>\n<p><img class=\"alignnone size-full wp-image-204035\" src=\"https://wpforms.com/wp-content/uploads/2018/05/Billing-Order-Form-Template.png\" alt=\"Billing-Order Form Template\" width=\"562\" height=\"334\" srcset=\"https://wpforms.com/wp-content/uploads/2018/05/Billing-Order-Form-Template.png 562w, https://wpforms.com/wp-content/uploads/2018/05/Billing-Order-Form-Template-300x178.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>WPForms will create a form for you.</p>\n<p>Near the bottom of the order form, on the right hand side, click on the form field labeled <strong>Available Items</strong>.</p>\n<p>When you do this, you’ll see on the left hand side section labeled <strong>Field Options</strong>. This is where you can rename the field, add or remove items for sale, and determine item prices.</p>\n<p><img class=\"alignnone size-full wp-image-302724\" src=\"https://wpforms.com/wp-content/uploads/2018/10/customize-form-fields.png\" alt=\"customize form fields\" width=\"562\" height=\"306\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/customize-form-fields.png 562w, https://wpforms.com/wp-content/uploads/2018/10/customize-form-fields-300x163.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You can also add additional fields to your form by dragging them from the left hand panel to the right hand panel.</p>\n<p>Then, click on the field to make any changes. You can also click on a form field and drag it to rearrange the order on your cancellation survey form.</p>\n<p>When your form looks the way you want it to, click <strong>Save</strong>.</p>\n<h3>Step 2: Configure Order Form Payment Settings</h3>\n<p>Once your order form is ready, you’ll need to configure it to work properly and accept payments from your customers.</p>\n<p>To do this, you’ll start by configuring the payment gateway so that payments process through your website.</p>\n<p>WPForms integrates with both <a href=\"https://wpforms.com/addons/paypal-standard-addon/\"> PayPal</a> and <a href=\"https://wpforms.com/addons/stripe-addon/\"> Stripe</a>, both of which are secure and popular ways to accept payments.</p>\n<p>In fact, WPForms now gives customers the ability to make recurring payments thanks to Stripe recurring. For help with this, check out our documentation on <a href=\"https://wpforms.com/docs/how-to-install-and-use-the-stripe-addon-with-wpforms\">setting up Stripe recurring functionality</a> on your WordPress forms.</p>\n<p>In addition, you can configure your order form to <a title=\"How to Allow Users to Choose a Payment Method on Your Form\" href=\"https://wpforms.com/docs/how-to-allow-users-to-choose-a-payment-method-on-your-form/\">give customers a choice</a> when it comes to payment method.</p>\n<p>For our example, we’ll configure PayPal to process orders.</p>\n<p>To start, install and activate the WPForms PayPal addon. Go to <strong>WPForms</strong> » <strong>Addons</strong> and click on <strong>Install Addon</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302727\" src=\"https://wpforms.com/wp-content/uploads/2018/10/paypal-addon.png\" alt=\"paypal addon\" width=\"562\" height=\"187\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/paypal-addon.png 562w, https://wpforms.com/wp-content/uploads/2018/10/paypal-addon-300x100.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Once activated, return to your order form to configure the PayPal settings. To do this, click on the <strong>Payments</strong> tab in the Form Editor and select <strong>PayPal</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302732\" src=\"https://wpforms.com/wp-content/uploads/2018/10/configure-paypal-settings.png\" alt=\"configure paypal settings\" width=\"562\" height=\"523\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/configure-paypal-settings.png 562w, https://wpforms.com/wp-content/uploads/2018/10/configure-paypal-settings-300x279.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, select the <strong>Enable PayPal Standard Payments </strong>checkbox, enter your PayPal email address, put the mode into <strong>Production</strong>, and configure any other settings as you see fit.</p>\n<p>Save your changes.</p>\n<h3>Step 3: Configure Your Order Form Settings</h3>\n<p>There are many settings to configure once you’ve customized your form to look the way you want it to.</p>\n<p><strong>General Settings</strong><br />\nTo start, go to <strong>Settings</strong> » <strong>General</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-246850\" src=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings.png\" alt=\"WPForms General Settings\" width=\"562\" height=\"443\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/08/WPForms-General-Settings-300x236.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Here you can change things like your form’s name, description, and submit button copy.</p>\n<p>You can also <a href=\"https://wpforms.com/how-to-build-spam-free-wordpress-contact-forms-the-ultimate-guide/\">enable additional spam prevention features</a> to <a href=\"https://www.wpbeginner.com/wordpress-security/\" target=\"_blank\" rel=\"noopener\">keep your website secure</a> from unwanted form submissions.</p>\n<p><strong>Form Confirmations</strong><br />\nForm confirmations are messages that display to site visitors once they submit an order form on your website. They let people know that their order has been processed and offer you the chance to let them know what the next steps are.</p>\n<p><img class=\"alignnone size-full wp-image-238574\" src=\"https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings.png\" alt=\"WPForms Confirmation Settings\" width=\"562\" height=\"260\" srcset=\"https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/07/WPForms-Confirmation-Settings-300x139.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>WPForms has three confirmation types:</p>\n<ol>\n<li><strong>Message.</strong>A simple message that displays once someone submits a form on your website thanking them and letting them know what the next steps are.</li>\n<li><strong>Show Page.</strong> Redirect users to another page on your website, such as a thank you page.</li>\n<li><strong>Go to URL (Redirect).</strong> Send site visitors to a different website.</li>\n</ol>\n<p>If you want to display different confirmations to site visitors based on the type of products or services they order, be sure to read up on <a href=\"https://wpforms.com/docs/how-to-create-conditional-form-confirmations/\">how to create conditional form confirmations</a>.</p>\n<p>Click <strong>Save</strong>.</p>\n<h3>Step 4: Configure Your Order Form Notifications</h3>\n<p>Notifications are a great way to send an email whenever an order form is submitted on your website.</p>\n<p><img class=\"alignnone size-full wp-image-246852\" src=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings.png\" alt=\"WPForms Notifications Settings\" width=\"562\" height=\"228\" srcset=\"https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings.png 562w, https://wpforms.com/wp-content/uploads/2018/08/WPForms-Notifications-Settings-300x122.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>In fact, unless you disable this feature, whenever someone submits an order form on your site, you’ll get a notification about it.</p>\n<p>Adding to that, form notifications are also a great way to send order form email receipts customers to customers after they submit an order and the form is processed.</p>\n<p>To create a form notification that will be sent to your customers, go to <strong>Settings</strong> » <strong>Notifications</strong>. Next, select <strong>Add New Notification</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302721\" src=\"https://wpforms.com/wp-content/uploads/2018/10/add-new-notification-2.png\" alt=\"add new notification\" width=\"562\" height=\"409\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/add-new-notification-2.png 562w, https://wpforms.com/wp-content/uploads/2018/10/add-new-notification-2-300x218.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>A modal will popup asking you to name your notification. Name it anything you want, since it’s for your reference only, and click <strong>OK</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302728\" src=\"https://wpforms.com/wp-content/uploads/2018/10/customer-notification.png\" alt=\"customer notification\" width=\"562\" height=\"360\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/customer-notification.png 562w, https://wpforms.com/wp-content/uploads/2018/10/customer-notification-300x192.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Next, click on the <strong>Show Smart Tags</strong> link under the section labeled <strong>Send to Email Address</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302733\" src=\"https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags.png\" alt=\"Form Notification Smart Tags\" width=\"562\" height=\"289\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags.png 562w, https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags-300x154.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, choose <strong>Email</strong> from the dropdown menu.</p>\n<p><img class=\"alignnone size-full wp-image-302734\" src=\"https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags-Email.png\" alt=\"Form Notification Smart Tags, Email\" width=\"562\" height=\"211\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags-Email.png 562w, https://wpforms.com/wp-content/uploads/2018/10/Form-Notification-Smart-Tags-Email-300x113.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>This will ensure that an email notification is sent to whatever email your customer put into your order form. Keep in mind that the number in the smart tag represents the field number on your order form.</p>\n<p>In other words, in our example, the 8th form field on our order form is an email form field.</p>\n<p>You can then customize the email subject, from name, from email, and reply-to fields using smart tags as well.</p>\n<p>Next, in the <strong>Message</strong> section, customize the message your customer will see in the email order receipt. For instance, thank them, let them know that their order was processed, tell them you’ll be in touch shortly, etc.</p>\n<p>Lastly, select individual smart tags from the dropdown to insert specific pieces of form submission information into the email receipt. For instance, you can add the items purchased and the total amount paid so your customer knows exactly what they bought and for how much.</p>\n<p>Or, if you want to include all form fields in the message portion of your WordPress order from email receipt, click on the smart tag labeled <code>{all_fields}</code>.</p>\n<p><img class=\"alignnone size-full wp-image-302726\" src=\"https://wpforms.com/wp-content/uploads/2018/10/notification-message-1.png\" alt=\"notification message\" width=\"562\" height=\"280\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/notification-message-1.png 562w, https://wpforms.com/wp-content/uploads/2018/10/notification-message-1-300x149.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you need to send out another notification, say to your manufacturer or supplier, just follow the same steps as above: add a new notification, change the send to email, customize the notification, and save your changes.</p>\n<h3>Step 5: Add Your Order Form to Your Website</h3>\n<p>After you’ve created an order from and customized the order from email receipt, you need to add it to your WordPress website.</p>\n<p>WPForms allows you to add your forms to many locations on your website, including your blog posts, pages, and even <a href=\"https://wpforms.com/how-to-add-a-contact-form-in-wordpress-sidebar-widget/\">sidebar widgets</a>.</p>\n<p>Let’s take a look at the most common post/page embed option.</p>\n<p>To start, create a new post or page in WordPress and then click on the <strong>Add Form</strong> button.</p>\n<p>Next, select your order form in the modal popup and click <strong>Add Form</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-302723\" src=\"https://wpforms.com/wp-content/uploads/2018/10/add-order-form.png\" alt=\"add order form\" width=\"562\" height=\"322\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/add-order-form.png 562w, https://wpforms.com/wp-content/uploads/2018/10/add-order-form-300x172.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Then, publish your post or page so your order form will appear on your website.</p>\n<p>And there you have it! You now know how to automatically send a WordPress order form email receipt using WPForms email notifications.</p>\n<p>If you want to allow customers to submit orders through your order form without paying right away, be sure to check out our roundup of the <a href=\"https://wpforms.com/the-best-invoicing-software-for-your-wordpress-order-forms/\">best invoicing software for your WordPress forms</a> so you can send out detailed invoices and collect payment when you’re ready.</p>\n<p>And if you aren’t sure whether WPForms is the right solution for your order form needs, hop on over to the <a href=\"https://wpforms.com/user-reviews/\">WPForms reviews section</a> and see what others have to say so you can decide for yourself.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-automatically-send-a-wordpress-order-form-email-receipt/\">How to Automatically Send a WordPress Order Form Email Receipt</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:88:\"https://wpforms.com/how-to-automatically-send-a-wordpress-order-form-email-receipt/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"4\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:8;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:65:\"How to Restrict Access to Logged in Users on Your WordPress Forms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:86:\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:95:\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 29 Oct 2018 12:00:12 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:6:{i:0;a:5:{s:4:\"data\";s:19:\"WordPress Tutorials\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:17:\"user registration\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:10:\"user login\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:19:\"form templates pack\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:17:\"form locker addon\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:20:\"restrict form access\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=302662\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:577:\"<p>Do you want a way to restrict access to logged in users on your WordPress forms? If you have a website that has registered users, there might come a time when you want to allow only those that are logged in to submit your forms. In this article, we’ll show you how to restrict access [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/\">How to Restrict Access to Logged in Users on Your WordPress Forms</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:9641:\"<p>Do you want a way to restrict access to logged in users on your WordPress forms? If you have a website that has registered users, there might come a time when you want to allow only those that are logged in to submit your forms.</p>\n<p>In this article, we’ll show you how to restrict access to logged in users on your WordPress forms so only those that are logged in can fill out and submit them.</p>\n<h3>Why Restrict Access to Your Online Forms?</h3>\n<p>Your website’s forms are usually available to anyone that arrives on your site. In fact, unless you have a specific reason for <a href=\"https://wpforms.com/how-to-limit-the-number-of-wordpress-form-entries/\">limiting form submissions</a>, everyone that needs to contact you while visiting your website typically has a way.</p>\n<p>That said, there may be times when you want only those that are logged in to your website to be able to submit your forms.</p>\n<p>Take a look at some examples:</p>\n<ul>\n<li>When you want to see how easy it is to use your site’s <a href=\"https://wpforms.com/how-to-create-a-user-registration-form-in-wordpress/\">registration form</a>, you might use a <a href=\"https://wpforms.com/create-a-survey-form-in-wordpress-step-by-step/\">survey</a> or <a href=\"https://wpforms.com/good-poll-questions-to-ask-on-your-forms-for-better-feedback/\">poll</a> form restricted to only those that are registered and logged in</li>\n<li>If you’re hosting an event that is only open to registered users and creating an <a href=\"https://wpforms.com/how-to-make-an-event-registration-form-in-wordpress/\">event registration form</a></li>\n<li>Maybe you’re hosting an event, such as an online webinar, that doesn’t require registration but rather just a simple confirmation of attendance, so you <a href=\"https://wpforms.com/how-to-create-an-rsvp-form-in-wordpress/\">create an online RSVP form</a></li>\n<li>You want logged in users to be able to submit support requests about their experience while logged in, so you <a href=\"https://wpforms.com/how-to-create-a-support-ticket-form-template-best-practices/\">create a support ticket form</a> that only they can submit</li>\n<li>If you&#8217;re running a contest that that is only open to those that have registered on your website</li>\n</ul>\n<p>And that’s only the beginning.</p>\n<p>No matter the reason you want to restrict access to logged in users, read on to see how WPForms helps you do it in a just a few simple steps.</p>\n<h3>Step 1: Create a New WordPress Form</h3>\n<p>The first thing you’ll need to do is install and activate the <a href=\"https://wpforms.com/pricing/\">WPForms plugin</a>. For more details, see this step by step guide on <a title=\"Step by step guide on How to Install a WordPress Plugin\" href=\"http://www.wpbeginner.com/beginners-guide/step-by-step-guide-to-install-a-wordpress-plugin-for-beginners/\" target=\"_blank\" rel=\"noopener\">how to install a plugin in WordPress</a>.</p>\n<p>Next, you’ll need to install and activate the <a href=\"https://wpforms.com/addons/form-locker-addon/\">Form Locker addon</a>.</p>\n<p>To do this, go to <strong>WPForms</strong> » <strong>Addon</strong> and find the one labeled <strong>Form Locker addon</strong>.</p>\n<p>Click <strong>Install Addon</strong> and then <strong>Activate</strong>.</p>\n<p><img class=\"alignnone size-full wp-image-290577\" src=\"https://wpforms.com/wp-content/uploads/2018/10/form-locker-addon.png\" alt=\"form locker addon\" width=\"562\" height=\"200\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/form-locker-addon.png 562w, https://wpforms.com/wp-content/uploads/2018/10/form-locker-addon-300x107.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Now you’ll need to create a new form.</p>\n<p>You can enable the form restriction feature on any WordPress form you create using WPForms. That’s because we strive to make form creation as easy as possible, while still offering our customer a ton of cool features for creating one-of-a-kind forms.</p>\n<p>For our example, we’ll create a rent receipt form using the <a href=\"https://wpforms.com/features/form-templates/\">Form Templates Pack addon</a>.</p>\n<p>This way, only people that log in and are ready to pay their rent can submit your rent receipt form.</p>\n<p>For help with this addon, that has over 100 different templates for you to choose from, check out this documentation on <a href=\"https://wpforms.com/docs/how-to-install-and-use-the-form-templates-pack-addon/\">installing and using the Form Templates Pack addon</a>.</p>\n<p>Once the Form Templates Pack addon is installed and activated, simply choose the <strong>Rent Receipt Form</strong> template and let WPForms create a form for you.</p>\n<p><img class=\"alignnone size-full wp-image-302669\" src=\"https://wpforms.com/wp-content/uploads/2018/10/rent-receipt-form-template.png\" alt=\"rent receipt form template\" width=\"562\" height=\"395\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/rent-receipt-form-template.png 562w, https://wpforms.com/wp-content/uploads/2018/10/rent-receipt-form-template-300x211.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You can add additional fields to your form by dragging them from the left hand panel to the right hand panel.</p>\n<p>Then, click on the field to make any changes. You can also click on a form field and drag it to rearrange the order on your WordPress form.</p>\n<p>When your form looks the way you want it to, click <strong>Save</strong>.</p>\n<h3>Step 2: Restrict Form Access to Logged in Users</h3>\n<p>Once your form is customized and saved, go to <strong>Settings</strong> » <strong>Form Locker</strong> in the Form Editor. This will bring up the different settings you can configure for the Form Locker addon.</p>\n<p><img class=\"alignnone size-full wp-image-293951\" src=\"https://wpforms.com/wp-content/uploads/2018/10/form-locker-settings-1.png\" alt=\"form locker settings\" width=\"562\" height=\"334\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/form-locker-settings-1.png 562w, https://wpforms.com/wp-content/uploads/2018/10/form-locker-settings-1-300x178.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>To start, click on the <strong>Enable restricting entries to logged in users only</strong> under the <strong>User</strong> checkbox to enable form restriction.</p>\n<p><img class=\"alignnone size-full wp-image-302667\" src=\"https://wpforms.com/wp-content/uploads/2018/10/form-restriction-checkbox.png\" alt=\"form restriction checkbox\" width=\"562\" height=\"337\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/form-restriction-checkbox.png 562w, https://wpforms.com/wp-content/uploads/2018/10/form-restriction-checkbox-300x180.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Once you check this box, a <strong>Message</strong> field will display.</p>\n<p>Here, you’ll draft a message that will appear to those that are not logged in to your site that are trying to submit a rent receipt form.</p>\n<p><img class=\"alignnone size-full wp-image-302668\" src=\"https://wpforms.com/wp-content/uploads/2018/10/form-restriction-message.png\" alt=\"form restriction message\" width=\"562\" height=\"331\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/form-restriction-message.png 562w, https://wpforms.com/wp-content/uploads/2018/10/form-restriction-message-300x177.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>You can add a simple message stating that your form is only for users that log in.</p>\n<p>Or, if you want to boost form conversions and build your business, you can add links to your registration form and <a href=\"https://wpforms.com/how-to-create-a-custom-login-form-for-improved-site-branding/\">user login form</a> so those that aren’t registered can register, and those that aren’t logged in can log in and fill out your form.</p>\n<p>Click <strong>Save</strong>.</p>\n<p>For more help with user registration and login forms, be sure to check out these articles:</p>\n<ul>\n<li><a href=\"https://wpforms.com/how-to-display-wordpress-login-and-register-forms-on-any-page/\">How to Display WordPress Login and Register Forms on Any Page</a></li>\n<li><a href=\"https://wpforms.com/user-registration-and-login-forms-for-wordpress-compared/\">User Registration and Login Forms for WordPress (Compared)</a></li>\n<li><a href=\"https://wpforms.com/how-to-set-up-wordpress-registration-redirects-complete-guide/\">How to Set Up WordPress Registration Redirects (Complete Guide)</a></li>\n<li><a href=\"https://wpforms.com/how-to-create-custom-wordpress-login-form/\">How to Completely Customize Your WordPress Login Form to Engage Users</a></li>\n</ul>\n<p>And there you have it! You now know how to restrict access to logged in users on your WordPress forms.</p>\n<p>If you want to learn how to better protect your WordPress forms, be sure to check out this tutorial on <a href=\"https://wpforms.com/how-to-password-protect-wordpress-forms-step-by-step/\">how to password protect your WordPress forms</a>.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/\">How to Restrict Access to Logged in Users on Your WordPress Forms</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:91:\"https://wpforms.com/how-to-restrict-access-to-logged-in-users-on-your-wordpress-forms/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:9;a:6:{s:4:\"data\";s:54:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n\n		\n		\n				\n			\n		\n		\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:61:\"4 Proven Ways to Convince Students to Open Recruitment Emails\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://wpforms.com/proven-ways-to-convince-students-to-open-recruitment-emails/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:88:\"https://wpforms.com/proven-ways-to-convince-students-to-open-recruitment-emails/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 25 Oct 2018 12:00:26 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:9:\"Marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:15:\"email marketing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:7:\"WPForms\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:17:\"list segmentation\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:14:\"email campaign\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://wpforms.com/?p=300219\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:627:\"<p>Are you wondering how to make the most of your institution’s recruiting emails? Learning how to convince students to open recruitment emails goes beyond basic email marketing skills. After all, students have an overwhelming number of options when they graduate high school and getting them to agree to consider your university can be tough. In [&#8230;]</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/proven-ways-to-convince-students-to-open-recruitment-emails/\">4 Proven Ways to Convince Students to Open Recruitment Emails</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Lindsay Liedke\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:12324:\"<p>Are you wondering how to make the most of your institution’s recruiting emails? Learning how to convince students to open recruitment emails goes beyond <a href=\"http://optinmonster.com/beginners-guide-to-email-marketing/\" target=\"_blank\" rel=\"noopener\">basic email marketing skills</a>. After all, students have an overwhelming number of options when they graduate high school and getting them to agree to consider your university can be tough.</p>\n<p>In this article, we’ll share some proven ways to convince students to open recruitment emails so you can establish your school as a top choice amongst prospective students.</p>\n<p>So, let’s get started.</p>\n<h3>1. Use a Powerful Online WordPress Form Builder</h3>\n<p>The first step to getting people to open your recruitment email campaigns is to capture email addresses of those that might be interested in attending your school. After all, without an email list you can’t send out emails, drive traffic to your site, and encourage people to apply to your school.</p>\n<p>And since <a href=\"https://beamery.com/academy/state-of-talent-acquisition-2017\" target=\"_blank\" rel=\"nofollow noopener\">85% of recruiters rely on email</a> to contact potential students and build relationships with them, it’s important you use a good online form solution to capture people’s email addresses.</p>\n<p>Luckily, <a href=\"https://wpforms.com\">WPForms</a> is perfect for doing just that. In fact, WPForms is the <strong>most beginner friendly WordPress form builder plugin</strong> on the market to date. It requires zero coding skills, comes with over a hundred pre-designed templates to get you started, and is simple to add anywhere on your website.</p>\n<p>If you need to capture email addresses so you can launch your recruitment campaigns, start with WPForms’ simple contact form template.</p>\n<p><img class=\"alignnone size-full wp-image-218831\" src=\"https://wpforms.com/wp-content/uploads/2018/06/Simple-Contact-Form.png\" alt=\"Simple Contact Form\" width=\"562\" height=\"372\" srcset=\"https://wpforms.com/wp-content/uploads/2018/06/Simple-Contact-Form.png 562w, https://wpforms.com/wp-content/uploads/2018/06/Simple-Contact-Form-300x199.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>From there, you can add and delete any form fields you want to customize it to meet your needs.</p>\n<p>That said, if your aim is to get people to subscribe so they can learn more about your school, see what it has to offer, and potentially apply, you might want to add some of these form fields in addition to the default Name, Email, and Comment or Message on our <a href=\"https://wpforms.com/how-to-create-a-simple-contact-form-in-wordpress/\">simple contact form</a>:</p>\n<ul>\n<li>Dropdown, Checkboxes, or Multiple Choice options so people can choose what major they’re interested in receiving information about</li>\n<li>A <a href=\"https://wpforms.com/how-to-add-a-gdpr-agreement-field-to-your-sites-forms/\">GDPR Agreement</a> field so people can agree to receiving marketing materials from you</li>\n<li>Phone and Date / Time fields so you can have a representative reach out at a convenient time to discuss options</li>\n<li>An Address field so you can mail additional information for those interested in learning more</li>\n</ul>\n<p>Take a look at how <a href=\"https://www.etoncollege.ca/admissions/contact-admissions-team\" target=\"_blank\" rel=\"nofollow noopener\">Eton College</a> adds form fields to their Request More Info form to help narrow down the type of information people want:</p>\n<p><img class=\"alignnone size-full wp-image-300222\" src=\"https://wpforms.com/wp-content/uploads/2018/10/form-field-example.png\" alt=\"form field example\" width=\"562\" height=\"591\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/form-field-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/form-field-example-285x300.png 285w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>The great thing about WPForms is that it integrates with the following email service providers:</p>\n<ul>\n<li><a href=\"https://wpforms.com/addons/mailchimp-addon/\">MailChimp</a></li>\n<li><a href=\"https://wpforms.com/addons/aweber-addon/\">AWeber</a></li>\n<li><a href=\"https://wpforms.com/addons/getresponse-addon/\">GetResponse</a></li>\n<li><a href=\"https://wpforms.com/features/constant-contact/\">Constant Contact</a></li>\n<li><a href=\"https://wpforms.com/addons/campaign-monitor-addon/\">Campaign Monitor</a></li>\n<li><a href=\"https://wpforms.com/addons/drip-addon/\">Drip</a></li>\n</ul>\n<p>Creating a highly converting contact form is the first step to convincing students to open your recruiting emails.</p>\n<h3>2. Segment Your Subscribers</h3>\n<p>There’s no sense sending out generic recruitment emails to all of your subscribers if your goal is to get students to open them. After all, potential students will be interested in receiving different information to suit their individual needs.</p>\n<p>That’s why segmenting your email lists is so helpful. In fact, <a href=\"https://mailchimp.com/resources/effects-of-list-segmentation-on-email-marketing-stats/\" target=\"_blank\" rel=\"nofollow noopener\">MailChimp claims</a> that segmented email lists result in 14.31% more email opens and 100.95% more clicks than non-segmented lists.</p>\n<p>See how MailChimp lets you create different email lists:</p>\n<p><img class=\"alignnone size-full wp-image-300224\" src=\"https://wpforms.com/wp-content/uploads/2018/10/segment-list-example.png\" alt=\"segment list example\" width=\"562\" height=\"633\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/segment-list-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/segment-list-example-266x300.png 266w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>If you aren’t sure where to begin when it comes to segmenting your subscribers, check out these ideas:</p>\n<ul>\n<li><strong>Stage in the Enrollment Process:</strong> New prospects and those that have opened recruitment emails before will need different email campaigns.</li>\n<li><strong>Location:</strong> international students will need different information about enrollment than local students. Even out-of-state students will have a different application process and tuition costs. If you want to learn about subscribers’ locations when they sign up, all you have to do is use the WPForms’ <a href=\"https://wpforms.com/addons/geolocation-addon/\">Geolocation addon</a>.</li>\n<li><strong>Interests:</strong> as we mentioned above, finding out what interests potential students, and using that data to create email campaigns, is much more effective than a general information email blast.</li>\n</ul>\n<p>Segmenting your email lists and sending out campaigns based on those lists will result in higher open and clickthrough rates. And who knows, you might even have a higher student enrollment for the year.</p>\n<h3>3. Focus on the Email Subject Line</h3>\n<p>If you can get students to subscribe to your newsletter or request more information, <strong>you’ve won half the battle</strong>. But getting them to actually open your emails can be tricky.</p>\n<p>But not if you focus on the email subject line.</p>\n<p>Here are some ways you can grab people’s attention from the start and get them to open your emails:</p>\n<ul>\n<li>Create a sense of urgency by adding important dates or deadlines. For instance, “Hurry, Apply Before the 15th to Guarantee Enrollment”</li>\n<li>Use the power of numbers to grab a student’s attention. For example, “5 Reasons Why Getting Your Degree at X University Rocks”</li>\n<li>Ask a question that will compel someone to open an email to find the answer. Try something like “Have You Heard About Our New Study Abroad Program?”</li>\n<li><a href=\"https://wpforms.com/email-personalization-examples/\">Personalize the subject line</a> using the student’s name. This will draw attention and build a better relationship. For example, try “Hey Lindsay, Learn About Why You Should Attend U of E!”</li>\n</ul>\n<p>Of course, these are just some examples of how you can stand out from the competition.</p>\n<p>If you want to make sure your email subject is amazing, check out the <a href=\"https://coschedule.com/email-subject-line-tester?utm_campaign=email-subject-line-tester-launch&amp;utm_medium=email&amp;utm_source=autopilot&amp;utm_term=free-email-subject-line-tester&amp;utm_content=eslt-2\" target=\"_blank\" rel=\"nofollow noopener\">CoSchedule Email Analyzer</a> and see for yourself:</p>\n<p><img class=\"alignnone size-full wp-image-300221\" src=\"https://wpforms.com/wp-content/uploads/2018/10/coschedule-email-headline-analyzer.png\" alt=\"coschedule email headline analyzer\" width=\"562\" height=\"419\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/coschedule-email-headline-analyzer.png 562w, https://wpforms.com/wp-content/uploads/2018/10/coschedule-email-headline-analyzer-300x224.png 300w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>A score of 40 above is what you should aim for so your campaigns get the most opens.</p>\n<p>Also, don’t forget to experiment with emojis, subject line length, and <a href=\"https://optinmonster.com/700-power-words-that-will-boost-your-conversions/\" target=\"_blank\" rel=\"noopener\"> power words</a> to boost opens and clickthroughs.</p>\n<h3>4. Watch Your Timing</h3>\n<p>Prospective students may sign up to receive information from a ton of potential schools they want to attend.</p>\n<p>If you don’t want to become a forgotten option, send out your first recruitment campaign within 24 hours of someone subscribing. This way they’ll remember subscribing and any information that drew them to your institution in the first place.</p>\n<p>For example, within minutes of requesting additional information from the <a href=\"https://www.phoenix.edu/\" target=\"_blank\" rel=\"nofollow noopener\">University of Phoenix</a>, an email was delivered:</p>\n<p><img class=\"alignnone size-full wp-image-300223\" src=\"https://wpforms.com/wp-content/uploads/2018/10/request-for-info-example.png\" alt=\"request for info example\" width=\"562\" height=\"601\" srcset=\"https://wpforms.com/wp-content/uploads/2018/10/request-for-info-example.png 562w, https://wpforms.com/wp-content/uploads/2018/10/request-for-info-example-281x300.png 281w\" sizes=\"(max-width: 562px) 100vw, 562px\" /></p>\n<p>Here are some of the highlights of this email encouraging people to open and engage with the email:</p>\n<ul>\n<li>The from name and email subject line is clear, though not that exciting, which reminds the subscriber they requested more information</li>\n<li>There are clickable links to the school’s homepage, programs, tuition information, credit transfer information, and even social media profiles</li>\n<li>There’s a phone number to call for those that prefer to talk to someone</li>\n</ul>\n<p>Sending emails right away shows students they are a priority and that you have lots to offer.</p>\n<p>And there you have it! Some of the best ways to cater to and convince students to open recruitment emails.</p>\n<p>If you want to make things even easier on students that not only open and engage with your recruitment emails, but actually want to apply to your institution, make sure to check out <a href=\"https://wpforms.com/how-to-require-payment-on-a-wordpress-file-upload-form/\">how to require payment on a WordPress file upload form</a>.</p>\n<p>This tutorial will help you add payment fields to the College Application template that is available in the <a href=\"https://wpforms.com/features/form-templates/\">Form Templates Pack addon</a> and is perfect for accepting college application fees from students directly through your form.</p>\n<p>So, what are you waiting for? Get started with the most powerful <a href=\"https://wpforms.com\">WordPress forms plugin</a> today.</p>\n<p>And don’t forget, if you like this article, then please follow us on <a href=\"https://www.facebook.com/wpforms\" target=\"_blank\" rel=\"nofollow noopener\">Facebook</a> and <a href=\"http://twitter.com/easywpforms\" target=\"_blank\" rel=\"nofollow noopener\">Twitter</a>.</p>\n<p>The post <a rel=\"nofollow\" href=\"https://wpforms.com/proven-ways-to-convince-students-to-open-recruitment-emails/\">4 Proven Ways to Convince Students to Open Recruitment Emails</a> appeared first on <a rel=\"nofollow\" href=\"https://wpforms.com\">WPForms</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:85:\"https://wpforms.com/proven-ways-to-convince-students-to-open-recruitment-emails/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:4:\"href\";s:25:\"https://wpforms.com/feed/\";s:3:\"rel\";s:4:\"self\";s:4:\"type\";s:19:\"application/rss+xml\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:44:\"http://purl.org/rss/1.0/modules/syndication/\";a:2:{s:12:\"updatePeriod\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"hourly\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:15:\"updateFrequency\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"1\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";O:42:\"Requests_Utility_CaseInsensitiveDictionary\":1:{s:7:\"\0*\0data\";a:9:{s:6:\"server\";s:12:\"nginx/1.15.2\";s:4:\"date\";s:29:\"Sat, 10 Nov 2018 15:46:41 GMT\";s:12:\"content-type\";s:34:\"application/rss+xml; charset=UTF-8\";s:10:\"set-cookie\";s:98:\"srv_id=587cf80a2a75f4f026cdf9547549c7f8; expires=Sat, 10-Nov-18 16:46:41 GMT; max-age=3600; path=/\";s:13:\"last-modified\";s:29:\"Fri, 09 Nov 2018 23:17:35 GMT\";s:4:\"etag\";s:34:\"\"ea3341b2ab625ffafaa70d265877c50d\"\";s:12:\"x-robots-tag\";s:15:\"noindex, follow\";s:9:\"x-backend\";s:22:\"awesomemotive-web1.com\";s:25:\"strict-transport-security\";s:13:\"max-age=86400\";}}s:5:\"build\";s:14:\"20130911040210\";}', 'no');
